INSERT INTO `exe_resource` VALUES (172, 51, 'plugin', 'iptv.order.rev.cq.java', '重庆接受订单数据', NULL, 'package com.exe.web.plugins;\n\nimport com.alibaba.fastjson.JSON;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport kkd.common.dao.dbuitl.SqlParameter;\nimport kkd.common.util.DateUtil;\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport javax.servlet.http.HttpServletRequest;\nimport java.sql.Types;\nimport java.util.*;\n\npublic class OrderRevCqCmd extends Cmd {\n    private final static Logger logger = LoggerFactory.getLogger(OrderRevCqCmd.class);\n    @Override\n    public void exe(CmdVo cmd, Map<String, String> param) {\n        Map<String, Object> vars=cmdHandle.getVars();\n        HttpServletRequest req= (HttpServletRequest) cmdHandle.getSys().get(\"req\");\n        Map<String,String> map=new HashMap<>();\n        String url=req.getParameter(\"url\");\n        map.put(\"url\",url);\n        map.put(\"stbID\",req.getParameter(\"stbID\"));\n        map.put(\"STBType\",req.getParameter(\"STBType\"));\n        map.put(\"mac\",req.getParameter(\"mac\"));\n        map.put(\"userAgent\",req.getHeader(\"User-Agent\"));\n        map.put(\"userID\",req.getParameter(\"userId\"));\n        map.put(\"user_id\",req.getParameter(\"userId\"));\n\n        map.put(\"token\",req.getParameter(\"token\"));\n        map.put(\"product_id\",req.getParameter(\"product_id\"));\n        map.put(\"platform\",req.getParameter(\"platform\"));\n        String data=JSON.toJSONString(map);\n        putLog(map.get(\"userID\"),\"\",map.get(\"token\"),map.get(\"product_id\"),data,\"未处理\");\n    }\n\n    /**\n     放入黑名单\n     */\n    public boolean putLog(String user_id,String user_type,String token,String product_id,String data,String description){\n        Date create_date=new java.util.Date();\n        String create_dateStr= DateUtil.format(create_date,\"yyyy-MM-dd HH:mm:ss\");\n        kkd.common.dao.dbuitl.JDBC.DbHelper dh=kkd.common.dao.dbuitl.JDBC.getDefaultNoTransactionDbHelper();\n        List<SqlParameter> parameters=new ArrayList<>();\n        try{\n            parameters.add(new SqlParameter(Types.VARCHAR,user_id));\n            parameters.add(new SqlParameter(Types.VARCHAR,user_type));\n            parameters.add(new SqlParameter(Types.VARCHAR,product_id));\n            parameters.add(new SqlParameter(Types.VARCHAR,create_dateStr));\n            parameters.add(new SqlParameter(Types.VARCHAR,description));\n            parameters.add(new SqlParameter(Types.VARCHAR,data));\n            String sql=\"insert into iptv_order_user(user_id,user_type,product_id,create_date,description,data)values\";\n            sql=sql+\"(?,?,?,?,?,?)\";\n            Integer cnt=dh.executeUpdate(sql,parameters);\n            if(cnt>0){\n                return true;\n            }\n        }catch (Exception e){\n            System.out.println(\"------rev data-------5-$e--\");\n            logger.error(\"\",e);\n        }\n        return false;\n    }\n\n    @Override\n    public String op() {\n        return null;\n    }\n}\n', NULL, NULL, '2021-03-08 22:23:52', '2021-03-08 22:53:36');
INSERT INTO `exe_version` VALUES (3173, '168', '资源', 'resource', 'package com.exe.web.plugins;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport com.exe.web.util.MyConfUtil;\nimport kkd.common.dao.dbuitl.JDBC;\nimport kkd.common.dao.dbuitl.SqlParameter;\nimport kkd.common.util.DateUtil;\nimport kkd.common.util.FileUtil;\nimport kkd.common.util.StringUtil;\nimport org.openqa.selenium.*;\nimport org.openqa.selenium.chrome.ChromeDriver;\nimport org.openqa.selenium.chrome.ChromeOptions;\nimport org.openqa.selenium.logging.LogEntries;\nimport org.openqa.selenium.logging.LogEntry;\nimport org.openqa.selenium.logging.LogType;\nimport org.openqa.selenium.logging.LoggingPreferences;\nimport org.openqa.selenium.remote.CapabilityType;\nimport org.openqa.selenium.remote.DesiredCapabilities;\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport javax.imageio.ImageIO;\nimport javax.servlet.http.HttpServletRequest;\nimport java.awt.image.BufferedImage;\nimport java.io.File;\nimport java.io.IOException;\nimport java.sql.ResultSet;\nimport java.sql.SQLException;\nimport java.sql.Types;\nimport java.util.*;\nimport java.util.concurrent.TimeUnit;\nimport java.util.logging.Level;\n\npublic class OrderCqCmd  extends Cmd{\n\n    private final static Logger logger = LoggerFactory.getLogger(OrderCmd.class);\n    //限制条数\n    private int limit=10;\n    //更新状态\n    private boolean updateData=true;\n    //截图\n    private boolean cutImg=true;\n    //调试\n    private boolean test=false;\n\n    public void exe(CmdVo caiAction, Map<String, String> param) {\n        try{\n            logger.info(\"执行-----------------start\");\n            Map<String, Object> sys=cmdHandle.getSys();\n            HttpServletRequest req= (HttpServletRequest) cmdHandle.getSys().get(\"req\");\n            String op=param.get(\"op\");\n            WebDriver driver= getWebDriver(sys);\n            Map<String,String> map=getData(req);\n            if(test){\n                String r=test(null,param,map,driver);\n                logger.info(\"执行结果：\"+r);\n                return;\n            }\n            Date zuoTian=DateUtil.addDays(new Date(),-30);\n            String date=DateUtil.format(zuoTian);\n            logger.info(\"find data create_date> $date \");\n\n            if(overLimit(date)){\n                return;\n            }else{\n                logger.debug(\"date:$date,未超过限制\");\n            }\n\n            logger.debug(\"get web driver:$driver\");\n\n            logger.debug(\"handle data:$map\");\n            Map<String,String> dataMap=StringUtil.getParaCollection(map.get(\"data\").toString(),\"&\",\"=\");\n            logger.debug(\"handle dataMap:$dataMap\");\n            String userId=map.get(\"user_id\").toString();\n            if(inBlack(userId)){\n                logger.debug(\"userId:$userId,黑名单中\");\n                map.put(\"description\",\"订购失败-黑名单用户\");\n                update(map);\n                return;\n            }else{\n                logger.debug(\"userId:$userId,不在黑名单中\");\n            }\n\n            logger.debug(\"userId:$userId,start runOrder\");\n            String r=test(map.get(\"id\").toString(),param,dataMap,driver);\n            if(r.indexOf(\"success\")==0){\n                map.put(\"description\",\"订购成功-\"+r);\n                logger.info(\"userId:\"+map.get(\"user_id\")+\",订购成功-\"+r);\n                update(map);\n            }else if(r.indexOf(\"exp\")==0){\n                map.put(\"description\",\"订购异常-\"+r);\n                logger.info(\"userId:\"+map.get(\"user_id\")+\",订购异常-\"+r);\n                update(map);\n            }else{\n                logger.info(\"userId:\"+map.get(\"user_id\")+\",订购失败-\"+r);\n                map.put(\"description\",\"订购失败-\"+r);\n                update(map);\n                putBlackUser(map);\n            }\n        }catch (Exception e){\n            logger.error(\"\",e);\n        }finally {\n            if(driver!=null){\n                driver.quit();\n            }\n            driver=null;\n            js=null;\n        }\n\n    }\n\n    private Map<String, String> getData(HttpServletRequest req) {\n        Map<String,String> map=new HashMap<>();\n        map.put(\"url\",req.getParameter(\"url\"));\n        map.put(\"stbID\",req.getParameter(\"stbID\"));\n        map.put(\"STBType\",req.getParameter(\"STBType\"));\n        map.put(\"mac\",req.getParameter(\"mac\"));\n        map.put(\"userAgent\",req.getHeader(\"userAgent\"));\n        return map;\n    }\n\n    public void getCode(){\n\n    }\n\n    public String test(String id,Map<String, String> param,Map<String,String> map,WebDriver driver){\n        try{\n            String url=map.get(\"url\");\n            driver.get(url);\n            JavascriptExecutor js= (JavascriptExecutor) driver;\n            exejs(js, map.get(\"StbID\"), map.get(\"STBType\"), map.get(\"mac\"));\n            if(has(driver.getCurrentUrl(),\"order_lancheck\")){\n                return \"order_lancheck\";\n            }\n            if(has(driver.getCurrentUrl(),\"order_second\")){\n                return \"order_second\";\n            }\n            if(has(driver.getCurrentUrl(),\"order_accountlock\")){\n                return \"order_accountlock\";\n            }\n            if(has(driver.getCurrentUrl(),\"order_intial\")){\n                List<String> pwds=new ArrayList<>();\n                pwds.add(\"666666\");\n                pwds.add(\"123456\");\n                pwds.add(\"111111\");\n                pwds.add(\"1234\");\n                pwds.add(\"000000\");\n                pwds.add(\"1111\");\n                for(String pwd:pwds){\n                    String jsStr=\" document.getElementById(\\\"password\\\").value=\'\" + pwd + \"\';\";\n                    js.executeScript(jsStr);\n                }\n                return \"\";\n            }\n            return \"other\";\n        }catch (Exception e){\n            logger.error(\"\",e);\n            e.printStackTrace();\n     