INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (5, 1, 'api', '/app/add', 'APP新增', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"应用新增\" corn=\"\">\n	<seg name=\"应用新增\">\n		<parse name=\"解析参数\" var=\"vo\"/>\n      <bean name=\"bean\" key=\"vo\" parseBody=\"true\">\n        <field name=\"应用名称\" key=\"appName\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"上下文\" key=\"contextPath\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n		\n		<exeSql name=\"插入数据\" var=\"insertCnt\" cols=\"id,vo.appName,vo.contextPath,createTime,vo.upTime\"\n			sql=\"insert into exe_app(id,appName,contextPath,createTime,upTime) values(?,?,?,?,?)\"\n		>\n			\n		</exeSql>\n		<if name=\"判断插入行数\" js=\"insertCnt>0\" true=\"插入成功\"  false=\"失败\"/>\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, NULL, '2020-11-23 20:34:49');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (6, 1, 'api', '/app/mod', 'APP修改', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"应用修改\" corn=\"\">\n	<seg name=\"应用修改\">\n      \n		<parse name=\"解析参数\" var=\"vo\" type=\"app\" />\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"id\" key=\"id\" mock=\"@integer(1, 10)\" reg=\"\"   verify=\"required\"/>\n        <field name=\"应用名称\" key=\"appName\" mock=\"@string(\'lower\', 5)\" reg=\"\"  verify=\"required\" />\n        <field name=\"上下文\" key=\"contextPath\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\"/>\n      </bean>\n		<var name=\"设置修改时间\" var=\"upTime\" type=\"date\"/>\n		\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"vo.appName,vo.contextPath,upTime,vo.id\"\n			sql=\"update exe_app set appName=?,contextPath=?,upTime=? where id=?\"\n		>\n			\n		</exeSql>\n		<if name=\"判断更新行数\" js=\"cnt>0\" true=\"更新成功\"  false=\"失败\"/>\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, NULL, '2020-11-23 20:34:22');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (7, 1, 'api', '/app/page', 'APP分页列表', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"应用查询\" corn=\"\">\n	<seg name=\"应用查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"应用名称\" var=\"appName\" key=\"appName\" />\n     <param name=\"上下文\" var=\"contextPath\" key=\"contextPath\" />\n		\n		<var name=\"sql\" var=\"sql\" init=\"select * from exe_app where 1=1 \"/>\n		<append name=\"添加appName\" key=\"sql\" when=\"appName\" value=\" and appName like concat(\'%\',?,\'%\') \"/>\n		<append name=\"添加contextPath\" key=\"sql\" when=\"contextPath\" value=\" and contextPath like concat(\'%\',?,\'%\') \"/>\n		<page name=\"查询数据\" var=\"list\" cols=\"id,appName,contextPath,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"appName,contextPath\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, NULL, '2020-11-25 10:18:26');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (9, 1, 'api', '/test/demo', '模板页面demo', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"跑单任务\" corn=\"1\">\n	<seg name=\"跑单任务\">\n        <param name=\"userId\" var=\"userId\" key=\"userId\" />\n        <param name=\"token\" var=\"token\" key=\"token\" />\n        <param name=\"user_type\" var=\"user_type\" key=\"user_type\" />\n        <param name=\"product_id\" var=\"product_id\" key=\"product_id\" />\n        <code name=\"解析数据\" hasBody=\"true\">\n        <![CDATA[\n             \n            def exe(a,cmdHandle){\n                def vars=cmdHandle.vars\n                org.slf4j.Logger LOGGER= org.slf4j.LoggerFactory.getLogger(\"order\");\n                LOGGER.info(\"----start-----userId=$vars.userId&token=$vars.token&user_type=$vars.user_type&product_id=$vars.product_id----start---end--\");\n            }\n         ]]>\n        </code>\n		<resp name=\"成功\" code=\"200\" msg=\"成功\" key=\"rs\" />\n	</seg>\n	\n</rule>', NULL, NULL, NULL, '2021-1-18 11:29:50');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (16, 2, 'api', '/resource/page', '资源分页', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"资源查询\" corn=\"\">\n	<seg name=\"资源查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"资源名称\" var=\"resourceName\" key=\"resourceName\" />\n     <param name=\"资源路径\" var=\"path\" key=\"path\" />\n     <param name=\"appId\" var=\"appId\" key=\"appId\" />\n     <param name=\"type\" var=\"type\" key=\"type\" />\n      \n		<var name=\"sql\" var=\"sql\" init=\"select id,type,path,ruleName resourceName,createTime,upTime from exe_resource where 1=1 \"/>\n			<append name=\"添加resourceName\" key=\"sql\" when=\"resourceName\" value=\" and ruleName like concat(\'%\',?,\'%\') \" t=\" like concat(\'%\',?,\'%\')\"/>\n      	  <append name=\"添加path\" key=\"sql\" when=\"path\" value=\" and path like concat(\'%\',?,\'%\') \"/>\n        <append name=\"添加appId\" key=\"sql\" when=\"appId\" value=\" and appId =? \"/>\n        <append name=\"添加type\" key=\"sql\" when=\"type\" value=\" and type=? \"/>\n		<page name=\"查询数据\" var=\"list\" cols=\"id,type,path,resourceName,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"resourceName,path,appId,type\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, NULL, '2020-11-19 10:06:05');
