INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (32, 4, 'api', '/file/save', '保存文件内容', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"保存文件\" corn=\"\">\n	<seg name=\"保存文件\">\n		<parse name=\"解析参数\" var=\"vo\" />\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"path\" key=\"path\" mock=\"@integer(1, 10)\" reg=\"\" verify=\"required\"/>\n        <field name=\"absPath\" key=\"absPath\" mock=\"@integer(1, 10)\" reg=\"\" />\n        <field name=\"data\" key=\"data\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n		<var name=\"path\" var=\"path\" key=\"vo.path\" decode=\"true\"/>\n        <var name=\"absPath\" var=\"absPath1\" key=\"vo.absPath\" decode=\"true\"/>\n		<var name=\"data\" var=\"data\" key=\"vo.data\"/>\n      <var name=\"设置时间\" var=\"upTime\" type=\"date\"/>\n      <var name=\"设置tableName\" var=\"tableName\" init=\"file\"/>\n      <var name=\"设置name\" var=\"name\" init=\"文件\"/>\n		\n		<file name=\"保存文件\" op=\"write\" var=\"list\" :dir=\"#{path}\" :absPath=\"#{absPath1}\" key=\"data\"/>\n      <exeSql name=\"插入历史数据\" var=\"cnt\" cols=\"path,name,tableName,vo.data,upTime\"\n			sql=\"insert into exe_version(sourceId,name,tableName,content,createTime) values(?,?,?,?,?)\"\n		>\n		</exeSql>\n		<resp name=\"保存成功\" code=\"200\" :msg=\"保存成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-2 11:19:34', '2021-3-30 15:32:16');
