INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (36, 32, 'api', '/scj/sc/add', '新增收藏夹', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"收藏夹新增\" corn=\"\">\n	<seg name=\"收藏夹新增\">\n		<parse name=\"解析参数\" var=\"vo\" type=\"scj\"/>\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"名称\" key=\"name\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"私有的\" key=\"private\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n        <field name=\"备注\" key=\"remark\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n        <field name=\"链接\" key=\"link\" mock=\"@string(\'lower\', 50)\" reg=\"\"  />\n      </bean>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n      \n      \n		<split name=\"拆分标签集合\" var=\"tagIds\" :key=\"#{vo.tagIds}\" split=\",\"/>\n		\n		<exeSql name=\"插入数据\"  rsId=\"scId\" cols=\"vo.name,vo.remark,vo.link,vo.private,createTime\"\n			sql=\"insert into scj_sc(name,remark,link,private,createTime) values(?,?,?,?,?)\"\n		>\n		</exeSql>\n      <exeSql name=\"删除标签关联\" var=\"cnt\" cols=\"scId\"\n			sql=\"delete from scj_tag_sc_ref where scId=?\"\n		>\n		</exeSql>\n      <count name=\"数据索引位置\" init=\"0\"  var=\"index\"/>\n      <go name=\"跳转循环新增标签\" to=\"循环新增标签\"/>\n	</seg>\n  \n  <seg name=\"循环新增标签\">\n    <var name=\"获取tagId\"  key=\"tagIds\" :index=\"#{index}\"  var=\"tagId\"  />\n    <if name=\"判断tagId是否存在\" js=\"tagId!=\'\'&amp;&amp; tagId!=null \" false=\"跳转到结束\" />\n    <exeSql name=\"插入标签数据\" var=\"insertCnt1\" cols=\"tagId,scId\"\n			sql=\"insert into scj_tag_sc_ref(tagId,scId) values(?,?)\"\n		>\n		</exeSql>\n     <count name=\"数据索引位置+1\"  inc=\"1\" var=\"index\"/>\n    <go name=\"跳转循环新增标签\" to=\"循环新增标签\"/>\n    <go name=\"跳转到结束\" to=\"结束\"/>\n  </seg>\n  <seg name=\"结束\">\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n  </seg>\n	\n</rule>', NULL, NULL, '2020-11-11 17:24:46', '2020-12-30 11:59:32');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (37, 32, 'api', '/scj/sc/mod', '收藏夹修改', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"搜藏修改\" corn=\"\">\n	<seg name=\"搜藏修改\">\n		<parse name=\"解析参数\" var=\"vo\" type=\"tag\"/>\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"id\" key=\"id\" mock=\"@integer(1, 10)\" reg=\"\" />\n        <field name=\"名称\" key=\"name\" mock=\"@string(\'lower\', 5)\" reg=\"\"  verify=\"required\"/>\n        <field name=\"私有的\" key=\"private\" mock=\"@string(\'lower\', 5)\" reg=\"\" />\n        <field name=\"备注\" key=\"remark\" mock=\"@string(\'lower\', 5)\" reg=\"\" />\n        <field name=\"标签id\" key=\"tagIds\" mock=\"@string(\'lower\', 5)\" reg=\"\" />\n        <field name=\"链接\" key=\"link\" mock=\"@string(\'lower\', 50)\" reg=\"\"  />\n      </bean>\n      <var name=\"scId\" var=\"scId\" key=\"vo.id\" />\n      <var name=\"设置当前时间\" var=\"upTime\" type=\"date\"/>\n      <user name=\"获取当前用户\" var=\"user\"/>\n      \n      <var name=\"userId\" childKey=\"id\" key=\"user\" var=\"userId\" />\n      <var name=\"userName\" childKey=\"userName\" key=\"user\" var=\"userName\" />\n      \n      <split name=\"拆分标签集合\" var=\"tagIds\" :key=\"#{vo.tagIds}\" split=\",\"/>\n\n      <exeSql name=\"修改数据\" var=\"insertCnt\" cols=\"vo.name,vo.private,vo.remark,vo.link,upTime,userId,userName,scId\"\n              sql=\"update scj_sc set name=?,private=?,remark=?,link=?,upTime=?,uid=?,userName=? where id=?\"\n              >\n\n      </exeSql>\n      <exeSql name=\"删除标签关联\" var=\"insertCnt\" cols=\"scId\"\n			sql=\"delete from scj_tag_sc_ref where scId=?\"\n		>\n		</exeSql>\n      <count name=\"数据索引位置\" init=\"0\"  var=\"index\"/>\n      <go name=\"跳转循环新增标签\" to=\"循环新增标签\"/>\n	</seg>\n	<seg name=\"循环新增标签\">\n        <var name=\"获取tagId\"  key=\"tagIds\" :index=\"#{index}\"  var=\"tagId\"  />\n        <if name=\"判断tagId是否存在\" js=\"tagId!=\'\'&amp;&amp; tagId!=null \" false=\"跳转到结束\" />\n        <exeSql name=\"插入标签数据\" var=\"insertCnt1\" cols=\"tagId,scId\"\n                sql=\"insert into scj_tag_sc_ref(tagId,scId) values(?,?)\"\n            >\n            </exeSql>\n         <count name=\"数据索引位置+1\"  inc=\"1\" var=\"index\"/>\n        <go name=\"跳转循环新增标签\" to=\"循环新增标签\"/>\n        <go name=\"跳转到结束\" to=\"结束\"/>\n    </seg>\n    <seg name=\"结束\">\n        <resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n        <go  name=\"跳转到结束\" to=\"end\"/>\n        <resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n        <empty name=\"end\"/>\n    </seg>\n</rule>', NULL, NULL, '2020-11-11 17:27:53', '2020-12-30 10:59:21');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (38, 32, 'api', '/scj/sc/del', '收藏夹删除', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"收藏删除\" corn=\"\">\n	<seg name=\"收藏删除\">\n		<param name=\"id\" var=\"id\" key=\"id\"/>\n		\n		<exeSql name=\"删除收藏\" var=\"cnt\" cols=\"id\"\n			sql=\"delete from  scj_sc where id=?\"\n		>\n			\n		</exeSql>\n      <exeSql name=\"删除关联标签\" var=\"cnt1\" cols=\"id\"\n			sql=\"delete from  scj_tag_sc_ref where scId=?\"\n		>\n			\n		</exeSql>\n		\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-11 17:33:56', '2020-11-13 14:02:28');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (39, 32, 'api', '/scj/sc/page', '收藏夹分页', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"收藏夹查询\" corn=\"\">\n	<seg name=\"收藏夹查询\">\n      <param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n      <param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n      <param name=\"收藏夹名称\" var=\"name\" key=\"name\" />\n      <param name=\"标签ID\" var=\"tagIds\" key=\"tagIds\" />\n		\n      <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1  \"/>\n      <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n      <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIds\" :value=\" and id in(	select scId from scj_tag_sc_ref where tagId in (#{tagIds}) )\"/>\n      \n		\n		<go name=\"跳转到结束\" to=\"结束\"/>\n	</seg>\n  <seg name=\"循环append标签1\">\n    <split name=\"拆分标签集合\" var=\"tagIdArr\" :key=\"#{tagIds}\" split=\",\"/>\n    <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1  \"/>\n    <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n    <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIdArr\" value=\" and id in(	select scId from scj_tag_sc_ref where tagId in\"/>\n    <code name=\"sql标签id拼接\" hasBody=\"true\">\n      def exe(cmd,cmdHandle){\n        def tagIds=cmdHandle.vars.tagIdArr\n        def oldSql=cmdHandle.vars.sql\n        def sql=\"(\"\n        def first=true\n        \n        for(tagId:tagIds){\n          if(first){\n            sql=sql+tagId\n              first=false\n          }else{\n         	 sql=sql+\",\"+tagId\n          }\n        }\n        sql=oldSql+sql+\")\"\n        print sql\n        cmdHandle.vars.put(\"sql\",sql)\n      }\n    </code>\n    <append name=\"添加右括号\" key=\"sql\" when=\"tagIdArr\" value=\" )\"/>\n    <go name=\"跳转到结束\" to=\"结束\"/>\n  </seg>\n  <seg name=\"结束1\">\n		<empty name=\"end\"/>\n  </seg>\n  <seg name=\"结束\">\n    <page name=\"查询数据\" var=\"list\" cols=\"id,name,private,remark,link,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"name\"\n		>\n		</page>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n  </seg>\n	\n</rule>', NULL, NULL, '2020-11-11 17:35:17', '2020-12-30 10:13:33');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (40, 32, 'api', '/scj/sc/init', '初始化收藏夹', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"收藏夹初始化\" corn=\"\">\n	<seg name=\"收藏夹初始化\">\n      <exeSql name=\"删除表\" var=\"insertCnt1\" cols=\"\" hasBody=\"true\">\n			drop table scj_sc\n		</exeSql>\n		<exeSql name=\"创建表\" var=\"insertCnt\" cols=\"\" hasBody=\"true\">\n        CREATE TABLE `scj_sc` (\n          `id` int(11) NOT NULL AUTO_INCREMENT,\n          `name` varchar(255) COLLATE utf8_bin DEFAULT NULL,\n          `remark` varchar(500) COLLATE utf8_bin DEFAULT NULL,\n          `createTime` datetime DEFAULT NULL,\n          `upTime` datetime DEFAULT NULL,\n          PRIMARY KEY (`id`)\n        ) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_bin\n		</exeSql>\n		\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-12 15:03:52', '2020-11-13 09:36:00');
