INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (55, 2, 'plugin', 'var.java', '变量设置与获取', NULL, 'package com.exe.base.plugins;\n\nimport java.lang.reflect.Method;\nimport java.net.URLDecoder;\nimport java.util.Date;\nimport java.util.List;\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.util.DateUtil;\nimport kkd.common.util.StringUtil;\nimport kkd.common.util.UUIDUtil;\n\n/**\n * 变量设置 或者 获取\n * @author tanbin\n *\n */\npublic class VarCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(VarCmd.class);\n	private String decode;\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		String var=param.get(\"var\");//设置变量\n		decode=param.get(\"decode\");//设置变量\n		String init=param.get(\"init\");\n		String type=param.get(\"type\");\n		String key=param.get(\"key\");//数据来源key\n		String childKey=param.get(\"childKey\");//通过子key获取\n		String indexStr=param.get(\"index\");//通过索引获取\n		String size=param.get(\"size\");//获取数组长度\n		Integer index=indexStr==null?null:Integer.parseInt(indexStr);\n        String debug=param.get(\"debug\");\n		\n		if(type!=null){\n			if(\"uuid\".equalsIgnoreCase(type)){\n				cmdHandle.getVars().put(var, UUIDUtil.getUUID());\n			}else if(\"date\".equalsIgnoreCase(type)){\n				String date=DateUtil.format(new Date(), DateUtil.DATETIME_PATTERN);\n				cmdHandle.getVars().put(var, date);\n			}\n			return;\n		}\n		if(init!=null){\n			cmdHandle.getVars().put(var, init);\n			return;\n		}\n        if(!StringUtil.isEmpty(caiAction.getBody())){\n            cmdHandle.getVars().put(var, caiAction.getBody());\n			return;\n        }\n		\n		Object data=getVal(cmdHandle.getVars(),key);\n		if(data==null){\n			cmdHandle.getVars().put(var, null);\n			return;\n		}\n		\n		//获取数组长度\n		if(size!=null){\n			if(data instanceof List){\n				List<?> list=(List<?>) data;\n				cmdHandle.getVars().put(var,list==null?0: list.size());\n			}else if(data.getClass().isArray() ){\n				Object[] list=(Object[]) data;\n				cmdHandle.getVars().put(var,list==null?0: list.length);\n			}\n			return;\n		}\n		\n		if(data instanceof List){\n			List<?> list=(List<?>) data;\n			if(index<list.size()){\n				data=list.get(index);\n			}else{\n				data=null;\n			}\n		}else if(data.getClass().isArray() ){\n			Object[] list=(Object[]) data;\n			if(index<list.length){\n				data=list[index];\n			}else{\n				data=null;\n			}\n		}\n		if(data==null){\n			cmdHandle.getVars().put(var, null);\n		}else if(data instanceof Map){\n			Map<String,Object> map=(Map) data;\n			Object obj=map.get(childKey);\n			cmdHandle.getVars().put(var, obj);\n		}else if(StringUtil.isEmpty(childKey)){\n			cmdHandle.getVars().put(var, data);\n		}else{\n			try {\n				Method m=data.getClass().getMethod(childKey);\n				Object obj=m.invoke(data);\n				cmdHandle.getVars().put(var, obj);\n			} catch (Exception e) {\n				logger.error(\"\", e);\n			}\n		}\n		\n	}\n\n	private Object getVal(Map<String, Object> vars, String string) {\n		if(string.contains(\".\")){\n			String[] arr=string.split(\"\\\\.\");\n			Object obj=null;\n			for (String string2 : arr) {\n				if(obj!=null){\n					if(obj instanceof Map){\n						Map<String,Object> map=(Map) obj;\n						obj=map.get(string2);\n						//是否解码\n						if(\"true\".equalsIgnoreCase(decode)){\n							if(obj instanceof String){\n								try {\n									obj=URLDecoder.decode((String)obj, \"utf-8\");\n								} catch (Exception e) {\n									logger.error(\"\", e);\n								}\n							}\n						}\n						return obj;\n					}\n				}else{\n					obj=vars.get(string2);\n					//是否解码\n					if(\"true\".equalsIgnoreCase(decode)){\n						if(obj instanceof String){\n							try {\n								obj=URLDecoder.decode((String)obj, \"utf-8\");\n							} catch (Exception e) {\n								logger.error(\"\", e);\n							}\n						}\n					}\n				}\n			}\n		}else{\n			return vars.get(string);\n		}\n		return null;\n	}\n	\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:44:47', '2021-1-2 10:34:29');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (56, 2, 'plugin', 'count.java', '计数器', NULL, 'package com.exe.base.plugins;\n\nimport java.util.Map;\n\nimport kkd.common.util.StringUtil;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class CountCmd extends Cmd {\n	@Override\n	public String op() {\n		return \"count\";\n	}\n	/**\n	 * 计数器\n	 * @param param\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> vars=cmdHandle.getVars();\n		String var=param.get(\"var\");\n		String initStr=param.get(\"init\");\n		String incStr=param.get(\"inc\");\n		String decStr=param.get(\"dec\");\n		Integer data=(Integer) vars.get(var);\n		data=data==null?0:data;\n		\n		\n		Integer init=null;\n		if(!StringUtil.isEmpty(initStr)){\n			init=Integer.parseInt(initStr);\n		}\n		\n		Integer inc=null;\n		if(!StringUtil.isEmpty(incStr)){\n			inc=Integer.parseInt(incStr);\n		}\n		\n		Integer dec=null;\n		if(!StringUtil.isEmpty(decStr)){\n			dec=Integer.parseInt(decStr);\n		}\n		if(init==null){\n			if(inc!=null){\n				vars.put(var, data+inc);\n				return ;\n			}\n			if(dec!=null){\n				vars.put(var, data-dec);\n				return ;\n			}\n		}else{\n			if(inc!=null){\n				vars.put(var, init+inc);\n				return ;\n			}\n			if(dec!=null){\n				vars.put(var, init-dec);\n				return ;\n			}\n		}\n		vars.put(var, init);\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:45:06', '2020-11-23 21:15:58');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (57, 2, 'plugin', 'print.java', '打印数据', NULL, 'package com.exe.base.plugins;\n\nimport java.util.Map;\n\nimport com.alibaba.fastjson.JSONObject;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\npublic class PrintCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(PrintCmd.class);\n\n    @Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		String var=param.get(\"var\");\n        String desc=param.get(\"desc\");\n		Object varData=cmdHandle.getVars().get(var);\n		String sys=param.get(\"sys\");\n		Object sysData=cmdHandle.getVars().get(sys);\n        if(desc==null){\n            desc=\"\";\n        }\n        def id=cmdHandle.getCaiRule().getId()\n        def name=cmdHandle.getCaiRule().getRuleName();\n		logger.info(\"--------debug-------id:\"+id+\",name=\"+name+\"-----\");\n        logger.info(\"--------debug-------\"+desc+\"-----\");\n		logger.info(\"--------输出var:{}\",JSONObject.toJSONString(varData));\n		logger.info(\"--------输出sys:{}\",JSONObject.toJSONString(sysData));\n		logger.info(\"--------debug-------end\");\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:45:28', '2021-1-26 11:33:07');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (58, 2, 'plugin', 'if.java', '条件判断', NULL, 'package com.exe.base.plugins;\n\nimport java.util.HashMap;\nimport java.util.Map;\n\nimport javax.script.ScriptEngine;\nimport javax.script.ScriptEngineManager;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.util.GroovyUtil;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.util.StringUtil;\n\n/**\n * 通过js执行判断\n * @author tanbin\n *\n */\npublic class IfCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(IfCmd.class);\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		String js=param.get(\"js\");\n		String trueVal=param.get(\"true\");\n		String falseVal=param.get(\"false\");\n		try {\n			boolean b=(boolean)GroovyUtil.parse(js, cmdHandle.getVars());\n			logger.debug(\"执行js:{},结果：{}\",js,b);\n			if(b){\n				if(!StringUtil.isEmpty(trueVal)){\n					cmdHandle.gotoNodeByName(caiAction,trueVal);\n				}\n			}else{\n				String var=param.get(\"var\");\n				String val=param.get(\"val\");\n				if(!StringUtil.isEmpty(val) && !StringUtil.isEmpty(var)){\n					cmdHandle.getVars().put(var, val);\n				}\n				cmdHandle.gotoNodeByName(caiAction,falseVal);\n			}\n		} catch (Exception e) {\n			logger.error(\"\", e);\n		}\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n	\n	private static ScriptEngine engine;\n	\n	private static ScriptEngine getJs() {\n		if(engine==null){\n			ScriptEngineManager scriptEngineManager = new ScriptEngineManager();\n	        engine = scriptEngineManager.getEngineByName(\"javascript\");\n		}\n        return engine;\n	}\n	\n	public static void main(String[] args) {\n		String s=\"\'ss\'!=\'\'\";\n		s=\"1+2\";\n		Map<String,String> map=new HashMap<>();\n		GroovyUtil.parse(s,null);\n	}\n}\n', NULL, NULL, '2020-11-23 17:45:46', '2020-11-23 21:16:07');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (59, 2, 'plugin', 'split.java', '字符串分割成数组', NULL, 'package com.exe.base.plugins;\n\nimport java.util.Map;\n\nimport kkd.common.util.StringUtil;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class SplitCmd extends Cmd {\n	@Override\n	public String op() {\n		return \"\";\n	}\n	/**\n	 * 字符串分割成数组\n	 * @param param\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> vars=cmdHandle.getVars();\n		String var=param.get(\"var\");\n		String key=param.get(\"key\");\n        String split=param.get(\"split\");\n        if(!StringUtil.isEmpty(key)){\n            String[] data=StringUtil.split(key, split);\n            vars.put(var, data);\n        }else{\n            vars.put(var, null);\n        }\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:46:07', '2020-11-23 21:16:11');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (60, 2, 'plugin', 'code.java', '代码片段', NULL, 'package com.exe.web.plugins;\n\nimport java.util.HashMap;\nimport java.util.Map;\nimport java.util.WeakHashMap;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.util.GroovyUtil;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport groovy.lang.GroovyClassLoader;\nimport groovy.lang.GroovyObject;\nimport kkd.common.util.Md5Util;\nimport kkd.common.util.StringUtil;\n\n/**\n * groovy 执行器\n * @author tanbin\n *\n */\npublic class CodeCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(CodeCmd.class);\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		try{\n            String code=cmd.getBody();\n            if(StringUtil.isEmpty(code)){\n                logger.error(\"{}节点：code 为空\", cmd.getName());\n                return;\n            }\n            Object[] cmdParam = new Object[2];\n            cmdParam[0]=cmd;\n            cmdParam[1]=cmdHandle;\n            GroovyUtil.exe(code, \"exe\", cmdParam);\n        }catch (Exception e){\n            Object id=cmdHandle.getCaiRule().getId()\n            Object name=cmdHandle.getCaiRule().getRuleName();\n            logger.error(\"--------error-------id:\"+id+\",name=\"+name+\"-----\");\n            e.printStackTrace();\n        }\n	}\n\n	public static void main(String[] args) {\n		GroovyClassLoader classLoader = new GroovyClassLoader();\n        Class groovyClass = classLoader.parseClass(\"def exe(cmd){\\n\" +\n                \"    print cmd.param.sql\\n\" +\n                \"}\");\n        try {\n			CmdVo cmd=new CmdVo();\n			Map<String,String> param=new HashMap<>();\n			param.put(\"sql\", \"select * from test\");\n			cmd.setParam(param);\n        	cmd.setName(\"test\");\n            Object[] param1 = { cmd};\n            GroovyObject groovyObject = (GroovyObject) groovyClass.newInstance();\n            groovyObject.invokeMethod(\"exe\",param1);\n        } catch (InstantiationException e) {\n            logger.error(\"\", e);\n        } catch (IllegalAccessException e) {\n            logger.error(\"\", e);\n        }\n	}\n	@Override\n	public String op() {\n		return null;\n	}\n	\n\n}\n', NULL, NULL, '2020-11-23 17:47:07', '2021-1-27 18:05:04');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (61, 2, 'plugin', 'header.java', 'HTTP头header操作', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\n\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\nimport kkd.common.util.StringUtil;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\n/**\n * 设置 、获取头信息\n * @author tanbin\n *\n */\npublic class HeaderCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		HttpServletResponse resp=(HttpServletResponse) sys.get(\"resp\");\n		\n		String val=param.get(\"val\");\n		String key=param.get(\"key\");\n		String var=param.get(\"var\");\n		if(!StringUtil.isEmpty(var)\n				&&!StringUtil.isEmpty(key)\n				){\n			Map<String, Object> vars=cmdHandle.getVars();\n			String header=req.getHeader(key);\n			vars.put(var, header);\n		}\n		if(!StringUtil.isEmpty(val)){\n			resp.addHeader(key, val);\n		}\n		\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:47:51', '2020-11-23 21:16:20');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (62, 2, 'plugin', 'param.java', 'HTTP参数操作', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\nimport java.util.regex.Matcher;\nimport java.util.regex.Pattern;\n\nimport javax.servlet.http.HttpServletRequest;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.exception.KKDValidationException;\nimport kkd.common.util.StringUtil;\n\n/**\n * 参数获取\n * @author tanbin\n *\n */\npublic class ParamCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		\n		Map<String, Object> vars=cmdHandle.getVars();\n		\n		String key=param.get(\"key\");\n		String var=param.get(\"var\");\n		String verify=param.get(\"verify\");\n		String reg=param.get(\"reg\");\n		String data=req.getParameter(key);\n		val(data, key, verify, reg);\n		vars.put(var, data);\n	}\n\n\n	public void val(String data,String fieldName,String verify,String reg){\n        if(!StringUtil.isEmpty(verify)){\n            String[] verrifys=StringUtil.split(verify, \"|\");\n            for (String ver : verrifys) {\n                if(\"required\".equalsIgnoreCase(ver)){\n                    if(StringUtil.isEmpty(data)){\n                        throw new KKDValidationException(fieldName+\"：不能为空！\");\n                    }\n                }else if(\"number\".equalsIgnoreCase(ver)){\n                    try{\n						Double.parseDouble(data);\n                    }catch(Exception e){\n                        throw new KKDValidationException(fieldName+\"：不是数字！\");\n                    }\n                }\n            }\n        }\n        if(!StringUtil.isEmpty(reg)){\n            Pattern r = Pattern.compile(reg);\n            Matcher m = r.matcher(data);\n            if(m.find()){\n                throw new KKDValidationException(fieldName+\"：验证失败！\");\n            }\n        }\n    }\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-23 17:48:11', '2020-11-23 21:16:24');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (63, 2, 'plugin', 'parse.java', 'JSON解析成对象', NULL, 'package com.exe.web.plugins;\n\nimport java.io.IOException;\nimport java.io.InputStream;\nimport java.util.Map;\n\nimport javax.servlet.http.HttpServletRequest;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport com.alibaba.fastjson.JSON;\nimport com.alibaba.fastjson.JSONObject;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.util.IOUtils;\n\npublic class ParseCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(ParseCmd.class);\n	\n	private JSONObject getVo() throws IOException {\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		InputStream is=req.getInputStream();\n		String str=IOUtils.toString(is, \"utf-8\");\n		logger.debug(\"parse data:{}\",str);\n		JSONObject vo=JSON.parseObject(str);\n		return vo;\n	}\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		Map<String, Object> vars=cmdHandle.getVars();\n		String var=param.get(\"var\");\n		try {\n			JSONObject json=getVo();\n			vars.put(var, json);\n		} catch (IOException e) {\n			logger.error(\"\", e);\n		}\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n}\n', NULL, NULL, '2020-11-23 17:48:36', '2020-11-23 21:16:34');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (64, 2, 'plugin', 'resp.java', 'HTTP响应', NULL, 'package com.exe.web.plugins;\n\nimport java.io.IOException;\nimport java.util.Map;\n\nimport javax.servlet.http.HttpServletResponse;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.entity.Msg;\n\n/**\n * 输出内容\n * @author tanbin\n *\n */\npublic class RespCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(RespCmd.class);\n	\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletResponse resp=(HttpServletResponse) sys.get(\"resp\");\n		\n		Map<String, Object> vars=cmdHandle.getVars();\n		String var=param.get(\"key\");\n		String code=param.get(\"code\");\n		String msg=param.get(\"msg\");\n		String tpl=param.get(\"tpl\");\n		try {\n			resp.setStatus(HttpServletResponse.SC_OK);\n			resp.setContentType(\"text/html; charset=utf-8\");\n			if(tpl==null){\n				Object jo= vars.get(var);\n				Msg<Object> rs=new Msg<Object>();\n				rs.setCode(Integer.parseInt(code));\n				rs.setMsg(msg);\n				rs.setV(jo);\n				resp.getWriter().write(rs.objectToFastJSON());\n			}else{\n				\n				String data=(String) cmdHandle.getVars().get(tpl);\n				resp.getWriter().write(data);\n			}\n		} catch (IOException e) {\n			logger.error(\"\", e);\n		}finally{\n		}\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:49:00', '2020-11-23 21:16:39');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (65, 2, 'plugin', 'sqlFind.java', 'sql查询', NULL, 'package com.exe.web.plugins;\n\nimport java.sql.ResultSet;\nimport java.sql.SQLException;\nimport java.sql.Types;\nimport java.util.ArrayList;\nimport java.util.HashMap;\nimport java.util.List;\nimport java.util.Map;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.dao.Paginator;\nimport kkd.common.dao.dbuitl.JDBC;\nimport kkd.common.dao.dbuitl.JDBC.DbHelper;\nimport kkd.common.dao.dbuitl.JDBC.MyBack;\nimport kkd.common.dao.dbuitl.SqlParameter;\nimport kkd.common.util.StringUtil;\n\n/**\n * 通过sql查询数据\n * @author tanbin\n *\n */\npublic class SqlFindCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(SqlFindCmd.class);\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		Integer page=1;\n		String sql=param.get(\"sql\");\n		String var=param.get(\"var\");\n		String paramStr=param.get(\"param\");\n		\n		String cols=param.get(\"cols\");\n		String[] colsAttr=cols.split(\",\");\n		String[] paramsAttr=paramStr.split(\",\");\n		Paginator p=new Paginator();\n		p.setPage(page);\n		\n		getJDBC(new MyBack() {\n			@SuppressWarnings(\"unchecked\")\n			public Object exe(DbHelper dh) throws Exception {\n				StringBuilder sb = new StringBuilder();\n				sb.append(sql);\n				List<SqlParameter> args=new ArrayList<SqlParameter>();\n				for (String string : paramsAttr) {\n					if(!StringUtil.isEmpty(string)){\n						Object obj=getVal(args, string);\n						args.add(new SqlParameter(Types.VARCHAR, obj));\n					}\n				}\n				logger.debug(\"执行sql:{}\",sql);\n				List<?> data=dh.executeQuery(sql, args, new JDBC.RowSetImpl() {\n					@Override\n					public Object getObjcet(ResultSet rs) {\n						Map<String,String> item=new HashMap<String, String>();\n						for (String string : colsAttr) {\n							try {\n								String value=rs.getString(string);\n								item.put(string, value);\n							} catch (SQLException e) {\n								logger.error(\"\", e);\n							}\n						}\n						return item;\n					}\n					\n				});\n				if(!data.isEmpty()){\n					cmdHandle.getVars().put(var, data);\n				}else{\n					cmdHandle.getVars().put(var, null);\n				}\n				return null;\n			}\n		});\n		\n	}\n\n	private Object getVal(List<SqlParameter> args, String string) {\n		if(string.contains(\".\")){\n			String[] arr=string.split(\"\\\\.\");\n			Object obj=null;\n			for (String string2 : arr) {\n				if(obj!=null){\n					if(obj instanceof Map){\n						Map<String,Object> map=(Map) obj;\n						return obj=map.get(string2);\n					}\n				}else{\n					obj=cmdHandle.getVars().get(string2);\n				}\n			}\n		}else{\n			return cmdHandle.getVars().get(string);\n		}\n		return null;\n	}\n	\n	public static void getJDBC(MyBack myBack){\n		JDBC jdbc=new JDBC();\n		jdbc.execute(myBack);\n	}\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-23 17:49:20', '2020-11-23 21:16:44');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (66, 2, 'plugin', 'exeSql.java', '执行SQL', NULL, 'package com.exe.web.plugins;\n\nimport java.sql.Types;\nimport java.util.ArrayList;\nimport java.util.List;\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.dao.dbuitl.JDBC;\nimport kkd.common.dao.dbuitl.JDBC.DbHelper;\nimport kkd.common.dao.dbuitl.JDBC.MyBack;\nimport kkd.common.dao.dbuitl.SqlParameter;\nimport kkd.common.util.StringUtil;\n\n\n/**\n * 通过sql查询数据\n * @author tanbin\n *\n */\npublic class ExeSqlCmd extends Cmd{\n	\n	private final static Logger logger = LoggerFactory.getLogger(ExeSqlCmd.class);\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		long l=System.currentTimeMillis();\n		String sql=param.get(\"sql\");\n		String rsId=param.get(\"rsId\");\n		String body=caiAction.getBody();\n		\n		String var=param.get(\"var\");\n		String cols=param.get(\"cols\");\n		String[] colsAttr=cols.split(\",\");\n		logger.debug(\"当前节点执行完成，耗时0：{}\",(System.currentTimeMillis()-l));\n		getJDBC(new MyBack() {\n			public Object exe(DbHelper dh) throws Exception {\n				logger.debug(\"当前节点执行完成，耗时1：{}\",(System.currentTimeMillis()-l));\n				List<SqlParameter> args=new ArrayList<SqlParameter>();\n				for (String string : colsAttr) {\n					if(!StringUtil.isEmpty(string)){\n						Object obj=getVal(args, string);\n						args.add(new SqlParameter(Types.VARCHAR, obj));\n					}\n				}\n				String sql1=sql;\n				if(!StringUtil.isEmpty(body)){\n					sql1=body;\n				}\n				logger.debug(\"exe sql:{}\",sql1);\n				logger.debug(\"当前节点执行完成，耗时2：{}\",(System.currentTimeMillis()-l));\n				if(!StringUtil.isEmpty(rsId)){\n					Integer id=dh.executeUpdateWithId(sql, args);\n					cmdHandle.getVars().put(rsId, id);\n				}else{\n					Integer cnt=dh.executeUpdate(sql1, args);\n					cmdHandle.getVars().put(var, cnt);\n				}\n				\n				logger.debug(\"当前节点执行完成，耗时3：{}\",(System.currentTimeMillis()-l));\n				return null;\n			}\n		});\n		logger.debug(\"当前节点执行完成，耗时4：{}\",(System.currentTimeMillis()-l));\n	}\n	\n	private Object getVal(List<SqlParameter> args, String string) {\n		if(string.contains(\".\")){\n			String[] arr=string.split(\"\\\\.\");\n			Object obj=null;\n			for (String string2 : arr) {\n				if(obj!=null){\n					if(obj instanceof Map){\n						Map<String,Object> map=(Map) obj;\n						return obj=map.get(string2);\n					}\n				}else{\n					obj=cmdHandle.getVars().get(string2);\n				}\n			}\n		}else{\n			return cmdHandle.getVars().get(string);\n		}\n		return null;\n	}\n	public static void getJDBC(MyBack myBack){\n		JDBC jdbc=new JDBC();\n		jdbc.execute(myBack);\n	}\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-23 17:49:38', '2020-11-23 21:16:47');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (67, 2, 'plugin', 'tpl.java', 'ftl模板', NULL, 'package com.exe.web.plugins;\n\nimport java.io.StringWriter;\nimport java.util.HashMap;\nimport java.util.Map;\n\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport com.exe.base.vo.ExeResourceVo;\nimport com.exe.bo.RuleBo;\n\nimport freemarker.cache.StringTemplateLoader;\nimport freemarker.template.Configuration;\nimport freemarker.template.Template;\nimport kkd.common.entity.Msg;\nimport kkd.common.util.StringUtil;\n\npublic class TplCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(TplCmd.class);\n	\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		HttpServletResponse resp=(HttpServletResponse) sys.get(\"resp\");\n		\n		String val=param.get(\"val\");\n		String src=param.get(\"src\");\n		String var=param.get(\"var\");\n		String model=param.get(\"model\");\n		if(!StringUtil.isEmpty(src)){\n			Msg<ExeResourceVo> msg=new Msg<>();\n			RuleBo.getByPath(src,msg);\n			if(msg.isSuccess()){\n				ExeResourceVo rule=msg.getV();\n				val=rule.getCaiConfig();\n			}\n		}\n		Object modelData=cmdHandle.getVars().get(model);\n		Map<String,String> dataModel=new HashMap<String, String>();\n		if(modelData!=null){\n			dataModel.put(model, modelData.toString());\n		}\n		String data=tpl(val, cmdHandle.getVars());\n		cmdHandle.getVars().put(var, data);\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n	\n	private String tpl(String tpl,Object model){\n		StringTemplateLoader stringLoader = new StringTemplateLoader();\n		String firstTemplate = \"firstTemplat\";\n		stringLoader.putTemplate(firstTemplate, tpl);\n		\n		Configuration cfg = new Configuration(Configuration.VERSION_2_3_28);\n		cfg.setTemplateLoader(stringLoader);\n		try {\n			Template template = cfg.getTemplate(firstTemplate);\n			StringWriter sw = new StringWriter();\n			template.process(model, sw);\n			logger.debug(\"{}\",sw.getBuffer().toString());\n			return sw.getBuffer().toString();\n		} catch (Exception e) {\n			logger.error(\"\", e);\n		} \n		return null;\n	}\n}\n', NULL, NULL, '2020-11-23 17:50:24', '2020-11-23 21:16:53');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (68, 2, 'plugin', 'page.java', 'SQL分页', NULL, 'package com.exe.web.plugins;\n\nimport java.sql.ResultSet;\nimport java.sql.ResultSetMetaData;\nimport java.sql.SQLException;\nimport java.sql.Types;\nimport java.util.ArrayList;\nimport java.util.HashMap;\nimport java.util.List;\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.dao.PageList;\nimport kkd.common.dao.Paginator;\nimport kkd.common.dao.dbuitl.JDBC;\nimport kkd.common.dao.dbuitl.JDBC.DbHelper;\nimport kkd.common.dao.dbuitl.JDBC.MyBack;\nimport kkd.common.dao.dbuitl.SqlParameter;\nimport kkd.common.util.StringUtil;\n\n/**\n * 通过sql查询数据\n * @author tanbin\n *\n */\npublic class PageCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(PageCmd.class);\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		String sql=param.get(\"sql\");\n		String var=param.get(\"var\");\n		String paramStr=param.get(\"param\");\n		String pageIndex=param.get(\"page\");\n		String pageSize=param.get(\"pageSize\");\n		//如果* 查所有\n		String cols=param.get(\"cols\");\n		String[] colsAttr=cols.split(\",\");\n		String[] paramsAttr=paramStr.split(\",\");\n		\n		getJDBC(new MyBack() {\n			@SuppressWarnings(\"unchecked\")\n			public Object exe(DbHelper dh) throws Exception {\n				\n				StringBuilder sb = new StringBuilder();\n				sb.append(sql);\n				List<SqlParameter> args=new ArrayList<SqlParameter>();\n				for (String string : paramsAttr) {\n					if(!StringUtil.isEmpty(string)){\n						Object obj=getVal(args, string);\n						if(!StringUtil.isEmpty(obj)){\n							args.add(new SqlParameter(Types.VARCHAR, obj));\n						}\n					}\n				}\n				logger.debug(\"执行sql:{}\",sql);\n				\n				JDBC.RowSetImpl<Object> rsi=new JDBC.RowSetImpl<Object>() {\n					@Override\n					public Object getObjcet(ResultSet rs) {\n						Map<String,String> item=new HashMap<String, String>();\n						if(\"*\".equalsIgnoreCase(cols)){\n							try{\n								ResultSetMetaData rsMeta=rs.getMetaData();\n								int columnCount=rsMeta.getColumnCount();\n								for (int i=1; i<=columnCount; i++) {\n									item.put(rsMeta.getColumnLabel(i), rs.getString(i));\n								}\n							}catch(Exception e){\n								logger.error(\"\", e);\n							}\n							\n						}else{\n							for (String string : colsAttr) {\n								try {\n									String value=rs.getString(string);\n									item.put(string, value);\n								} catch (SQLException e) {\n									logger.error(\"\", e);\n								}\n							}\n						}\n						return item;\n					}\n					\n				};\n				Paginator p=new Paginator();\n				p.setPage(Integer.parseInt(pageIndex));\n				p.setItemsPerPage(Integer.parseInt(pageSize));\n				PageList<Object> data=dh.query(sql, args, p, rsi);\n				if(data!=null){\n					cmdHandle.getVars().put(var, data);\n				}else{\n					cmdHandle.getVars().put(var, null);\n				}\n				return null;\n			}\n		});\n		\n	}\n\n	private Object getVal(List<SqlParameter> args, String string) {\n		if(string.contains(\".\")){\n			String[] arr=string.split(\"\\\\.\");\n			Object obj=null;\n			for (String string2 : arr) {\n				if(obj!=null){\n					if(obj instanceof Map){\n						Map<String,Object> map=(Map) obj;\n						return obj=map.get(string2);\n					}\n				}else{\n					obj=cmdHandle.getVars().get(string2);\n				}\n			}\n		}else{\n			return cmdHandle.getVars().get(string);\n		}\n		return null;\n	}\n	\n	public static void getJDBC(MyBack myBack){\n		JDBC jdbc=new JDBC();\n		jdbc.setDebug(true);\n		jdbc.execute(myBack);\n	}\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-23 17:50:42', '2020-12-30 17:50:10');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (69, 2, 'plugin', 'append.java', '字符串append', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\n\nimport kkd.common.util.StringUtil;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\n/**\n * 字符串操作\n * @author tanbin\n *\n */\npublic class AppendCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		String key=param.get(\"key\");\n		String when=param.get(\"when\");\n		String value=param.get(\"value\");\n		Object whenVal= cmdHandle.getVars().get(when);\n		if(!StringUtil.isEmpty(whenVal)){\n			String keyVal=(String) cmdHandle.getVars().get(key);\n			cmdHandle.getVars().put(key, keyVal+value);		\n		}\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:51:06', '2020-11-23 21:17:00');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (70, 2, 'plugin', 'file.java', '文件相关操作', NULL, 'package com.exe.web.plugins;\n\nimport java.io.File;\nimport java.io.FileReader;\nimport java.io.IOException;\nimport java.io.LineNumberReader;\nimport java.util.ArrayList;\nimport java.util.List;\nimport java.util.Map;\n\nimport java.net.URLDecoder;\n\nimport com.alibaba.fastjson.JSONObject;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport com.exe.web.util.MyConfUtil;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.util.FileUtil;\nimport kkd.common.util.StringUtil;\n\npublic class FileCmd extends Cmd{\n	private final static Logger logger = LoggerFactory.getLogger(FileCmd.class);\n	\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n//		logger.setRuleId(cmdHandle.getCaiRule().getId().toString());\n		String op=param.get(\"op\");\n        String currPath=param.get(\"currPath\");\n        String absPath=param.get(\"absPath\");\n		String path=param.get(\"dir\");\n		String var=param.get(\"var\");\n		String key=param.get(\"key\");\n        String root=param.get(\"root\");\n        root=root==null?\"\":root;\n        String oldRootPath=MyConfUtil.getRootPath()+File.separator+\"static\";\n		String rootPath=MyConfUtil.getRootPath()+File.separator+\"static\"+File.separator+root;\n		\n		\n		if(StringUtil.isEmpty(path)){\n			path=rootPath;\n            if(!StringUtil.isEmpty(currPath)){\n                path=rootPath+currPath;\n            }\n            if(!StringUtil.isEmpty(absPath)){\n                path=absPath;\n            }\n		}else{\n            if(!StringUtil.isEmpty(absPath)){\n                path=absPath+path;\n            }else{\n                path=rootPath+path;\n            }\n			\n		}\n		if(\"rename\".equalsIgnoreCase(op)){\n			String dest=param.get(\"dest\");\n			File f=new File(path);\n			File destFile=new File(dest);\n			boolean r=false;\n			r = f.renameTo(destFile);\n			cmdHandle.getVars().put(var, r);\n		}else if(\"newfile\".equalsIgnoreCase(op)){\n			File f=new File(path);\n			boolean r=false;\n			try {\n				r = f.createNewFile();\n			} catch (IOException e) {\n				logger.error(\"\", e);\n			}\n			cmdHandle.getVars().put(var, r);\n		}else if(\"mkdir\".equalsIgnoreCase(op)){\n			String folder=param.get(\"folder\");\n			boolean r=FileUtil.mkdir(path+\"/\"+folder);\n			cmdHandle.getVars().put(var, r);\n		}else if(\"del\".equalsIgnoreCase(op)){\n			File f=new File(path);\n			boolean r=f.delete();\n			cmdHandle.getVars().put(var, r);\n		}else if(\"read\".equalsIgnoreCase(op)){\n			//开始行\n			String startLineStr=param.get(\"startLine\");\n			Long startLine = null;\n			String limitStr=param.get(\"limit\");\n			Long limit=null;\n			\n			if(startLine==null && !StringUtil.isEmpty(startLineStr) ) {\n				startLine=Long.parseLong(startLineStr);\n			}else {\n				startLine=0l;\n			}\n			\n			if(limit==null && !StringUtil.isEmpty(limitStr)) {\n				limit=Long.parseLong(limitStr);\n			}else {\n				limit=Long.MAX_VALUE;\n			}\n			FileReader file;\n			try {\n				InputStream is=new FileInputStream(path);\n                InputStreamReader isr=new InputStreamReader(is,\"utf-8\");\n                LineNumberReader lineNumberReader =new LineNumberReader(isr);\n				if(startLine==-1){\n					long c=lineNumberReader.skip(Long.MAX_VALUE);\n					cmdHandle.getVars().put(var,c);\n					return;\n				}\n				lineNumberReader.skip(startLine);\n				long hasRead=0;\n				StringBuilder sb=new StringBuilder();\n				String line=null;\n				do{\n					line=lineNumberReader.readLine();\n					if(line!=null){\n						sb.append(line);\n						sb.append(\"\\r\\n\");\n						hasRead++;\n					}\n					\n				}while(line!=null && hasRead<=limit-1);\n				cmdHandle.getVars().put(var,sb);\n			} catch (Exception e) {\n				logger.error(\"\", e);\n			}\n			\n		}else if(\"write\".equalsIgnoreCase(op)){\n			String data=(String) cmdHandle.getVars().get(key);\n			//FileUtil.writeMethodB(path, data, false);\n            try {\n                Writer writer = new BufferedWriter(\n                    new OutputStreamWriter(\n                            new FileOutputStream(path,false), \"UTF-8\"));\n            \n                writer.write(data);\n                writer.close();\n            } catch (IOException var4) {\n                var4.printStackTrace();\n            }\n			cmdHandle.getVars().put(var,true);\n		}else if(\"list\".equalsIgnoreCase(op)){\n			File f=new File(path);\n			File[] list=f.listFiles();\n			List<JSONObject> listFile=new ArrayList<JSONObject>(); \n			for (File file : list) {\n				JSONObject obj=new JSONObject();\n				String type=\"\";\n				String fileName=file.getName();\n           \n				if(file.isDirectory()){\n					type=\"directory\";\n				}else{\n					int pos=fileName.lastIndexOf(\".\");\n					if(pos!=-1){\n						String subfix=fileName.substring(pos+1);\n						type=subfix;\n					}\n				}\n				if(\"png\".equalsIgnoreCase(type)\n						||\"gif\".equalsIgnoreCase(type)\n						||\"jpg\".equalsIgnoreCase(type)\n						){\n					String thumb=file.getPath().replace(rootPath, \"\");\n					//obj.put(\"thumb\",\"/html\"+thumb);\n              obj.put(\"thumb\",thumb);\n				}\n                if(StringUtil.isEmpty(absPath)){\n                    String url=file.getPath().replace(rootPath, \"\");\n				    obj.put(\"url\",url);\n                }else{\n                    String url=file.getPath().replace(absPath, \"\");\n				    obj.put(\"url\",url);\n                }\n				\n				obj.put(\"type\",type);\n				obj.put(\"name\", file.getName());\n                if(StringUtil.isEmpty(absPath)){\n                    path=StringUtil.replace(file.getPath(), rootPath, \"\");\n				    obj.put(\"path\", path);\n                }else{\n                    path=StringUtil.replace(file.getPath(), absPath, \"\");\n				    obj.put(\"path\", path);\n                }\n				\n				listFile.add(obj);\n			}\n			cmdHandle.getVars().put(var, listFile);\n		}\n	}\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n	public static void main(String[] args) {\n		File f=new File(\"E:\\\\workspaceGroup\\\\workspace_my\\\\exeWeb\\\\static\\\\html\\\\test\\\\test.txt\");\n		logger.debug(\"{}\",f.delete());\n	}\n}\n', NULL, NULL, '2020-11-23 17:51:22', '2021-3-30 11:45:08');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (71, 2, 'plugin', 'bean.java', '定义bean方便验证api生成', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\nimport java.util.regex.Matcher;\nimport java.util.regex.Pattern;\n\nimport com.alibaba.fastjson.JSONArray;\nimport com.alibaba.fastjson.JSONObject;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.exception.KKDValidationException;\nimport kkd.common.util.StringUtil;\n\n\n/**\n * 定义实体操作\n * @author tanbin\n *\n */\npublic class BeanCmd extends Cmd{\n\n	@Override\n	public void exe(final CmdVo caiAction, final Map<String, String> param) {\n        final String key=param.get(\"key\");\n        final JSONArray fields =caiAction.getJsonBody();\n        JSONObject data=(JSONObject) cmdHandle.getVars().get(key);\n        if(fields!=null){\n            for (final Object object : fields) {\n                final JSONObject field= (JSONObject) object;\n                String fieldName=field.getString(\"key\");\n                String verify=field.getString(\"verify\");\n                String reg=field.getString(\"reg\");//正则表达式\n                val(data, fieldName, verify, reg);\n            }\n        }\n    }\n    \n    public void val(JSONObject data,String fieldName,String verify,String reg){\n        if(!StringUtil.isEmpty(verify)){\n            String[] verrifys=StringUtil.split(verify, \"|\");\n            for (String ver : verrifys) {\n                if(\"required\".equalsIgnoreCase(ver)){\n                    String val=data.getString(fieldName);\n                    if(StringUtil.isEmpty(val)){\n                        throw new KKDValidationException(fieldName+\"：不能为空！\");\n                    }\n                }else if(\"number\".equalsIgnoreCase(ver)){\n                    try{\n                        data.getDouble(fieldName);\n                    }catch(Exception e){\n                        throw new KKDValidationException(fieldName+\"：不是数字！\");\n                    }\n                }\n            }\n        }\n        if(!StringUtil.isEmpty(reg)){\n            Pattern r = Pattern.compile(reg);\n            String val=data.getString(fieldName);\n            Matcher m = r.matcher(val);\n            if(m.find()){\n                throw new KKDValidationException(fieldName+\"：验证失败！\");\n            }\n        }\n    }\n\n	@Override\n	public String op() {\n		return null;\n	}\n\n}\n', NULL, NULL, '2020-11-23 17:51:53', '2020-11-23 21:17:07');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (72, 43, 'api', '/db/sql/exe', '执行SQL', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"sql执行\" corn=\"\">\n	<seg name=\"sql执行\">\n		<param name=\"sql\" var=\"sql\" key=\"sql\" mock=\"@string(\'lower\', 5)\" />\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"\"\n			:sql=\"#{sql}\"\n		>\n		</exeSql>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"cnt\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-23 20:32:32', '2020-11-23 20:42:09');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (73, 43, 'api', '/db/sql/query', '数据库查询', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"应用查询\" corn=\"\">\n	<seg name=\"应用查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"sql\" var=\"sql\" key=\"sql\" mock=\"@string(\'lower\', 5)\" />\n		\n		<page name=\"查询数据\" var=\"list\" cols=\"*\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"\"\n		>\n			\n		</page>\n		<print name=\"d\" var=\"list\"/>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-23 20:38:54', '2020-11-25 09:57:13');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (74, 47, 'api', '/user/reg', '用户注册', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"用户新增\" corn=\"\">\n	<seg name=\"用户新增\">\n		<parse name=\"解析参数\" var=\"vo\"/>\n      <bean name=\"bean\" key=\"vo\" parseBody=\"true\">\n        <field name=\"用户名\" key=\"userName\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"昵称\" key=\"nickName\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"密码\" key=\"pwd\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n      <code name=\"md5\" hasBody=\"true\">\n          def exe(a,cmdHandle){\n          		def pwd=cmdHandle.getVars().get(\"vo\").pwd\n          		def pwd_md5=kkd.common.util.Md5Util.encode(pwd)\n          		cmdHandle.getVars().put(\"pwd\",pwd_md5)\n          }\n      </code>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n		\n		<exeSql name=\"插入数据\" var=\"insertCnt\" cols=\"id,vo.userName,vo.nickName,pwd,createTime,vo.upTime\"\n			sql=\"insert into user_info(id,userName,nickName,pwd,createTime,upTime) values(?,?,?,?,?)\"\n		>\n			\n		</exeSql>\n		<if name=\"判断插入行数\" js=\"insertCnt>0\" true=\"插入成功\"  false=\"失败\"/>\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-24 21:00:18', '2021-1-17 21:32:38');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (75, 35, 'api', '/user/login', '用户登录', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"用户登录\" corn=\"\">\n	<seg name=\"用户登录\">\n		<param name=\"用户名\" var=\"userName\" key=\"userName\" />\n		<param name=\"密码\" var=\"pwd\" key=\"pwd\" />\n		\n		<code name=\"md5\" hasBody=\"true\">\n          def exe(a,cmdHandle){\n          		def pwd=cmdHandle.getVars().get(\"pwd\")\n          		def pwd_md5=kkd.common.util.Md5Util.encode(pwd)\n          		cmdHandle.getVars().put(\"pwd\",pwd_md5)\n          }\n      </code>\n		<sqlFind name=\"查询数据\" var=\"list\" cols=\"id,userName\" param=\"userName,pwd\"\n		sql=\"select id,userName from user_info where 1=1 and userName=? and pwd=?\"  \n		>\n			\n		</sqlFind>\n      <if name=\"用户是否存在\" js=\"list!=null &amp;&amp; list.size>0\" false=\"失败\"/>\n       <code name=\"获取用户\" hasBody=\"true\">\n              def exe(a,cmdHandle){\n                    if(cmdHandle.vars.list!=null){\n                        def user=cmdHandle.vars.list.get(0)\n         			    cmdHandle.vars.put(\"user\",user)\n                    }\n        			\n              }\n          </code>\n      <!-- <var name=\"获取用户\" var=\"user\" key=\"list\" index=\"0\"/> -->\n      <cookie name=\"获取cookie\" op=\"get\" key=\"sessionId\" var=\"sessionId\"/>\n     \n      \n      <if name=\"判断cookie是否存在\" js=\"sessionId==null\" false=\"记录用户会话\" />\n      		<code name=\"uuid\" hasBody=\"true\">\n              def exe(a,cmdHandle){\n                def uuid=kkd.common.util.UUIDUtil.getUUID()\n                cmdHandle.getVars().put(\"sessionId\",uuid)\n              }\n          </code>\n      		<cookie name=\"设置cookie\" op=\"put\" key=\"sessionId\" value=\"sessionId\" uri=\"/\" maxAge=\"999999\"/>\n      <!-- 记录用户会话 -->\n		<session name=\"记录用户会话\" :key=\"#{sessionId}\" op=\"put\" value=\"user\" timeout=\"999999\" />\n      \n  