INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (76, 47, 'api', '/user/page', '用户列表', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"用户查询\" corn=\"\">\n	<seg name=\"用户查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"用户名\" var=\"userName\" key=\"userName\" />\n     <param name=\"昵称\" var=\"nickName\" key=\"nickName\" />\n		\n		<var name=\"sql\" var=\"sql\" init=\"select * from user_info where 1=1 \"/>\n		<append name=\"添加userName\" key=\"sql\" when=\"userName\" value=\" and userName like concat(\'%\',?,\'%\') \" />\n		<append name=\"添加nickName\" key=\"sql\" when=\"nickName\" value=\" and nickName like concat(\'%\',?,\'%\') \" />\n		<page name=\"查询数据\" var=\"list\" cols=\"id,userName,nickName,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"userName,nickName\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-24 21:01:40', '2020-11-25 09:26:29');
