ql name=\"插入标签数据\" var=\"insertCnt1\" cols=\"roleId,vo.id\"\n                sql=\"insert into user_role_ref(roleId,userId) values(?,?)\"\n            >\n            </exeSql>\n        <count name=\"数据索引位置+1\"  inc=\"1\" var=\"index\"/>\n        \n        \n        <go name=\"跳转循环新增角色\" to=\"循环新增角色\"/>\n        <go name=\"跳转到结束\" to=\"结束\"/>\n    </seg>\n    <seg name=\"结束\">\n        <resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n        <go  name=\"跳转到结束\" to=\"end\"/>\n        <resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n        <empty name=\"end\"/>\n    </seg>\n	\n</rule>', NULL, NULL, '2020-11-24 21:27:51', '2020-12-21 11:54:18');
