INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (86, 2, 'plugin', 'session.java', '会话管理', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\n\nimport com.alibaba.fastjson.JSON;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.cache.server.MapServerThread;\nimport kkd.common.util.StringUtil;\n\n\n/**\n * session 操作\n * @author tanbin\n *\n */\npublic class SessionCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		MapServerThread st =MapServerThread.getInstance();\n		String op=param.get(\"op\");\n		if(\"remove\".equalsIgnoreCase(op)){\n			String key=param.get(\"key\");\n			st.remove(key);\n		}else if(\"get\".equalsIgnoreCase(op)){\n			String key=param.get(\"key\");\n			String innerArg=param.get(\"innerArg\");\n			String inner=param.get(\"inner\");\n			String upDate=param.get(\"upDate\");\n			Boolean updateDate=Boolean.valueOf(upDate);\n			String var=param.get(\"var\");\n			String value=null;\n			if(!StringUtil.isEmpty(key)){\n				if(inner!=null&&\"true\".equalsIgnoreCase(inner)){\n					value=st.getInnerData(key,innerArg, updateDate);\n				}else{\n					value=st.get(key,updateDate);\n				}\n			}\n			cmdHandle.getVars().put(var, value);\n		}else if(\"put\".equalsIgnoreCase(op)){\n			String key=param.get(\"key\");\n			String timeoutStr=param.get(\"timeout\");\n			String value=param.get(\"value\");\n			Object o=cmdHandle.getVars().get(value);\n			String data=JSON.toJSONString(o);\n			st.put(key, Long.valueOf(timeoutStr), data);\n		}\n		\n	}\n\n\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-25 11:07:16', '2020-11-25 13:50:41');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (87, 47, 'api', '/user/current', '当前用户', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"当前用户\" corn=\"\">\n	<seg name=\"当前用户\">\n      <cookie name=\"获取cookie\" key=\"sessionId\" op=\"get\" var=\"sessionId\"/>\n      \n      <!-- 获取用户会话 -->\n		<session name=\"记录用户会话\" :key=\"#{sessionId}\" upDate=\"true\" op=\"get\" var=\"user\"/>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"user\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-25 11:14:02', '2020-12-21 11:07:36');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (88, 2, 'plugin', 'cookie.java', 'cookie操作', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\n\nimport javax.servlet.http.Cookie;\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.util.CookieUtil;\n\n\n/**\n * cookie 操作\n * @author tanbin\n *\n */\npublic class CookieCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n        Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		HttpServletResponse resp=(HttpServletResponse) sys.get(\"resp\");\n		String op=param.get(\"op\");\n		if(\"remove\".equalsIgnoreCase(op)){\n            String path=param.get(\"path\");\n            String domain=param.get(\"domain\");\n            String name=param.get(\"key\");\n            CookieUtil.remove(resp, path, domain, name);\n		}else if(\"get\".equalsIgnoreCase(op)){\n            String name=param.get(\"key\");\n            String var=param.get(\"var\");\n            Cookie val=CookieUtil.getCookieByName(req, name);\n            if(val!=null){\n                cmdHandle.getVars().put(var, val.getValue());\n            }else{\n                cmdHandle.getVars().put(var, null);\n            }\n		}else if(\"put\".equalsIgnoreCase(op)){\n            String domain=param.get(\"domain\");\n            String name=param.get(\"key\");\n            String value=param.get(\"value\");\n            String uri=param.get(\"uri\");\n            \n            String valueData=cmdHandle.getVars().get(value).toString();\n            String maxAgeStr=param.get(\"maxAge\");\n            CookieUtil.addCookie(resp, name, valueData, uri, domain, Integer.valueOf(maxAgeStr));\n		}\n		\n	}\n\n\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-25 14:13:31', '2020-12-27 20:26:16');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (89, 2, 'plugin', 'route.java', '路由操作', NULL, 'package com.exe.web.plugins;\n\nimport java.io.InputStream;\nimport java.io.OutputStream;\nimport java.net.HttpURLConnection;\nimport java.net.URL;\nimport java.util.Enumeration;\nimport java.util.List;\nimport java.util.Map;\n\nimport javax.servlet.ServletInputStream;\nimport javax.servlet.ServletOutputStream;\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\n\n/**\n * 路由 操作\n * @author tanbin\n *\n */\npublic class RouteCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n        HttpServletResponse resp=(HttpServletResponse) sys.get(\"resp\");\n        String url=param.get(\"url\");\n        String location=param.get(\"location\");\n        send(req, resp, url, location);\n		\n	}\n\n    private void send(HttpServletRequest req,HttpServletResponse resp,String url,String location) {\n		HttpURLConnection con=null;\n		try {\n			Enumeration<String> head=req.getHeaderNames();\n			String path=req.getServletPath();\n			if(location!=null){\n				int pos=path.indexOf(location);\n				path=path.substring(pos+location.length());\n//				path=path.replace(location, \"\");\n			} \n			url=url+(path==null?\"\":path);\n			url=url+(req.getQueryString()==null?\"\":\"?\"+req.getQueryString());\n			System.out.println(String.format(\"请求URL:%s\", url));\n			System.setProperty(\"sun.net.http.allowRestrictedHeaders\", \"true\");\n			URL u = new URL(url);\n			con = (HttpURLConnection) u.openConnection();\n			con.setConnectTimeout(3000);\n			con.setReadTimeout(3000);\n			con.setRequestMethod(req.getMethod());\n			con.setDoOutput(true);\n			con.setDoIn