INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (90, 2, 'plugin', 'dbinfo.java', '数据库信息获取', NULL, 'package com.exe.web.plugins;\n\nimport java.util.List;\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport kkd.common.util.StringUtil;\nimport kkd.common.dao.dbuitl.JDBC;\nimport kkd.common.dao.dbuitl.JDBC.DbHelper;\nimport kkd.common.dao.dbuitl.JDBC.MyBack;\n\n\n/**\n * 通过sql查询数据\n * @author tanbin\n *\n */\npublic class DbInfoCmd extends Cmd{\n	\n	private final static Logger logger = LoggerFactory.getLogger(DbInfoCmd.class);\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n        String op=param.get(\"op\");\n        String var=param.get(\"var\");\n		getJDBC(new MyBack() {\n			public Object exe(DbHelper dh) throws Exception {\n                String catalog=param.get(\"catalog\");\n                String schemaPattern=param.get(\"schemaPattern\");\n				String tableNamePattern=param.get(\"tableNamePattern\");\n				catalog=\"\".equals(catalog)?null:catalog;\n				schemaPattern=\"\".equals(schemaPattern)?null:schemaPattern;\n				tableNamePattern=\"\".equals(tableNamePattern)?null:tableNamePattern;\n                if(\"table\".equalsIgnoreCase(op)){\n					String[] types=new String[2];\n					types[0]=\"TABLE\";\n					types[1]=\"VIEW\";\n                    List<Map<String,String>> list=dh.listAllTables(catalog, schemaPattern, tableNamePattern, types);\n                    cmdHandle.getVars().put(var, list);\n                }else if(\"field\".equalsIgnoreCase(op)){\n                    String columnNamePattern=param.get(\"columnNamePattern\");\n                    if(StringUtil.isEmpty(columnNamePattern)){\n                        columnNamePattern=\"%\";\n                    }\n                    List<Map<String,String>> list=dh.listAllFields(catalog, schemaPattern, tableNamePattern, columnNamePattern);\n                    //List<Map<String,String>> list=dh.listAllFields(null, \"%\", tableNamePattern, \"%\");\n                    cmdHandle.getVars().put(var, list);\n                }\n				return null;\n			}\n		});\n	}\n	\n	public static void getJDBC(MyBack myBack){\n		JDBC jdbc=new JDBC();\n		jdbc.execute(myBack);\n	}\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-11-26 09:46:30', '2021-1-18 10:16:13');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (91, 2, 'api', '/db/sql/dbinfo', '数据库信息', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"数据库信息查询\" corn=\"\">\n	<seg name=\"数据库信息查询\">\n      <param name=\"op\" var=\"op\" key=\"op\" mock=\"@string(\'lower\', 5)\" />\n      <param name=\"catalog\" var=\"catalog\" key=\"catalog\" mock=\"@string(\'lower\', 5)\" />\n      <param name=\"schemaPattern\" var=\"schemaPattern\" key=\"schemaPattern\" mock=\"@string(\'lower\', 5)\" />\n      <param name=\"tableNamePattern\" var=\"tableNamePattern\" key=\"tableNamePattern\" mock=\"@string(\'lower\', 5)\" />\n      <param name=\"columnNamePattern\" var=\"columnNamePattern\" key=\"columnNamePattern\" mock=\"@string(\'lower\', 5)\" />\n      \n		<dbinfo name=\"查询数据\" var=\"list\" :op=\"#{op}\"  :catalog=\"#{catalog}\" :schemaPattern=\"#{schemaPattern}\" :tableNamePattern=\"#{tableNamePattern}\" :columnNamePattern=\"#{columnNamePattern}\"\n		>\n		</dbinfo>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-11-26 09:57:14', '2020-11-26 10:52:05');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (92, 2, 'plugin', 'shell.java', 'linux命令', NULL, 'package com.exe.web.plugins;\n\nimport java.util.List;\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\n\nimport java.io.IOException;\nimport java.io.InputStream;\nimport java.util.Scanner;\nimport java.util.concurrent.TimeUnit;\n\n\n\n/**\n * linux 命令\n * @author tanbin\n *\n */\npublic class ShellCmd extends Cmd{\n	\n	private final static Logger logger = LoggerFactory.getLogger(ShellCmd.class);\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n        String var=param.get(\"var\");\n        String cmdKey=param.get(\"cmd\");\n        String[] cmds=cmdHandle.getVars().get(cmdKey);\n        String rs=\"\";\n//         for(String cmd:cmds){\n//             rs=rs+run(cmd);\n//         }\n        rs=rs+run(cmds);\n        cmdHandle.getVars().put(var, rs);\n	}\n    \n   public static String run(String command) throws IOException {\n		Scanner input = null;\n		String result = \"\";\n		Process process = null;\n		try {\n			process = Runtime.getRuntime().exec(command);\n			try {\n				//等待命令执行完成\n				process.waitFor(10, TimeUnit.SECONDS);\n			} catch (InterruptedException e) {\n				e.printStackTrace();\n			}\n			InputStream is = process.getInputStream();\n			input = new Scanner(is);\n			while (input.hasNextLine()) {\n				result += input.nextLine() + \"\\n\";\n			}\n			result = command + \"\\n--result:\" + result; //加上命令本身，打印出来\n		} finally {\n			if (input != null) {\n				input.close();\n			}\n			if (process != null) {\n				process.destroy();\n			}\n		}\n		return result;\n	}\n	\n    \n	public static String run(String[] command) throws IOException {\n		Scanner input = null;\n		String result = \"\";\n		Process process = null;\n		try {\n//             command=getOsCmd() command[0];\n        String cmdStr=null;\n        for(String cmd:command){\n            if(cmdStr==null){\n                cmdStr=cmd;\n            }else{\n                cmdStr=cmdStr+\";\"+cmd\n            }\n            \n        }\n        process=Runtime.getRuntime().exec(new String[]{\"/bin/sh\",\"-c\",cmdStr});\n//             String[] cmd = new String[3];\n//             cmd[0]=\"/bin/sh\";\n//             cmd[1]=\"-c\";\n//             cmd[2]=\"cd /app;ls -l\";\n//             cmd={\"/bin/sh\",\"-c\",\"cd /app; ls -l\"}\n//             process=Runtime.getRuntime().exec(cmd);\n// 			process = Runtime.getRuntime().exec(command);\n			try {\n				//等待命令执行完成\n				process.waitFor(10, TimeUnit.SECONDS);\n			} catch (InterruptedException e) {\n				e.printStackTrace();\n			}\n			InputStream is = process.getInputStream();\n			input = new Scanner(is);\n			while (input.hasNextLine()) {\n				result += input.nextLine() + \"\\n\";\n			}\n			result = command + \"\\n--result:\" + result; //加上命令本身，打印出来\n		} finally {\n			if (input != null) {\n				input.close();\n			}\n			if (process != null) {\n				process.destroy();\n			}\n		}\n		return result;\n	}\n    public List<String> run1(String[] commands) {\n        List<String> rspList = new ArrayList<String>();\n        Runtime run = Runtime.getRuntime();\n        try {\n            Process proc = run.exec(\"/bin/bash\", null, null);\n            BufferedReader in = new BufferedReader(new InputStreamReader(proc.getInputStream()));\n            PrintWriter out = new PrintWriter(new BufferedWriter(new OutputStreamWriter(proc.getOutputStream())), true);\n            for (String line : commands) {\n                out.println(line);\n            }\n            // out.println(\"cd /home/test\");\n            // out.println(\"pwd\");\n            // out.println(\"rm -fr /home/proxy.log\");\n            out.println(\"exit\");// 这个命令必须执行，否则in流不结束。\n            String rspLine = \"\";\n            while ((rspLine = in.readLine()) != null) {\n                System.out.println(rspLine);\n                rspList.add(rspLine);\n            }\n            proc.waitFor();\n            in.close();\n            out.close();\n            proc.destroy();\n        } catch (IOException e1) {\n            e1.printStackTrace();\n        } catch (InterruptedException e) {\n            e.printStackTrace();\n        }\n        return rspList;\n    }\n    \n    public static String getOsCmd(){\n        Properties props=System.getProperties(); //获得系统属性集\n        String osName = props.getProperty(\"os.name\"); //操作系统名称\n        if(osName.toLowerCase().contains(\"linux\")){\n            return \"/bin/sh -c\";\n        }else if(osName.toLowerCase().contains(\"windows\")){\n            return \"cmd /c\";\n        }else{\n            throw new RuntimeException(\"服务器不是linux|windows操作系统\");\n        }\n    }\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-12-14 15:22:40', '2021-2-18 18:05:29');
