INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (97, 2, 'api', '/resource/index/add', '资源索引新增', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"资源索引新增\" corn=\"\">\n	<seg name=\"资源索引新增\">\n		<parse name=\"解析参数\" var=\"vo\"/>\n      <bean name=\"bean\" key=\"vo\" parseBody=\"true\">\n        <field name=\"path\" key=\"path\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"type\" key=\"type\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"md5\" key=\"md5\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"state\" key=\"state\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n		\n		<exeSql name=\"插入数据\" var=\"insertCnt\" cols=\"id,vo.path,vo.type,vo.md5,vo.state,createTime,vo.upTime\"\n			sql=\"insert into resource_index(id,path,type,md5,state,createTime,upTime) values(?,?,?,?,?,?,?)\"\n		>\n			\n		</exeSql>\n		<if name=\"判断插入行数\" js=\"insertCnt>0\" true=\"插入成功\"  false=\"失败\"/>\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-12-16 10:01:49', '2020-12-16 11:13:00');
