INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (109, 47, 'api', '/user/logout', '用户登出', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"用户登出\" corn=\"\">\n	<seg name=\"用户登出\">\n        <cookie name=\"获取cookie\" op=\"get\" key=\"sessionId\" var=\"sessionId\"/>\n        \n        <if name=\"判断cookie是否存在\" js=\"sessionId!=null\" false=\"end\" />\n        \n        <cookie name=\"删除cookie\" op=\"remove\" key=\"sessionId\" path=\"/\" />\n		<session name=\"记录用户会话\" :key=\"#{sessionId}\" op=\"remove\"  />\n      \n      \n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"登出失败\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-12-27 10:01:15', '2020-12-27 20:24:25');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (110, 47, 'plugin', 'user.java', '获取当前用户', NULL, 'package com.exe.web.plugins;\n\nimport java.util.Map;\n\nimport com.alibaba.fastjson.JSON;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport kkd.common.cache.server.MapServerThread;\nimport kkd.common.util.StringUtil;\nimport kkd.common.util.CookieUtil;\n\nimport javax.servlet.http.Cookie;\nimport javax.servlet.http.HttpServletRequest;\nimport javax.servlet.http.HttpServletResponse;\n\n/**\n * user 操作\n * @author tanbin\n *\n */\npublic class UserCmd extends Cmd{\n\n	@Override\n	public void exe(CmdVo cmd, Map<String, String> param) {\n        Map<String, Object> sys=cmdHandle.getSys();\n		HttpServletRequest req=(HttpServletRequest) sys.get(\"req\");\n		MapServerThread st =MapServerThread.getInstance();\n        Cookie val=CookieUtil.getCookieByName(req, \"sessionId\");\n        String var=param.get(\"var\");\n        if(val!=null){\n            String sessionId=val.getValue();\n            String value=st.get(sessionId,false);\n            if(value!=null){\n                //{id:\"1\",userName:\"zhangsan\"}\n                com.alibaba.fastjson.JSONObject jo=com.alibaba.fastjson.JSONObject.parseObject(value);\n                cmdHandle.getVars().put(var, jo);\n            }else{\n                cmdHandle.getVars().put(var, null);\n            }\n        }else{\n            cmdHandle.getVars().put(var, null);\n        }\n	}\n\n\n	\n	@Override\n	public String op() {\n		return null;\n	}\n	\n}\n', NULL, NULL, '2020-12-27 20:38:55', '2021-1-2 21:19:51');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (111, 32, 'api', '/scj/sc/public', '公开收藏', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"公开收藏查询\" corn=\"\">\n	<seg name=\"公开收藏查询\">\n      <param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n      <param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n      <param name=\"收藏夹名称\" var=\"name\" key=\"name\" />\n      <param name=\"标签ID\" var=\"tagIds\" key=\"tagIds\" />\n		\n      <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1 and (private=\'\' or private is null )  \"/>\n      <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n      <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIds\" :value=\" and id in(	select scId from scj_tag_sc_ref where tagId in (#{tagIds}) )\"/>\n      \n		\n		<go name=\"跳转到结束\" to=\"结束\"/>\n	</seg>\n  <seg name=\"循环append标签1\">\n    <split name=\"拆分标签集合\" var=\"tagIdArr\" :key=\"#{tagIds}\" split=\",\"/>\n    <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1  \"/>\n    <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n    <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIdArr\" value=\" and id in(	select scId from scj_tag_sc_ref where tagId in\"/>\n    <code name=\"sql标签id拼接\" hasBody=\"true\">\n      def exe(cmd,cmdHandle){\n        def tagIds=cmdHandle.vars.tagIdArr\n        def oldSql=cmdHandle.vars.sql\n        def sql=\"(\"\n        def first=true\n        \n        for(tagId:tagIds){\n          if(first){\n            sql=sql+tagId\n              first=false\n          }else{\n         	 sql=sql+\",\"+tagId\n          }\n        }\n        sql=oldSql+sql+\")\"\n        print sql\n        cmdHandle.vars.put(\"sql\",sql)\n      }\n    </code>\n    <append name=\"添加右括号\" key=\"sql\" when=\"tagIdArr\" value=\" )\"/>\n    <go name=\"跳转到结束\" to=\"结束\"/>\n  </seg>\n  <seg name=\"结束1\">\n		<empty name=\"end\"/>\n  </seg>\n  <seg name=\"结束\">\n    <page name=\"查询数据\" var=\"list\" cols=\"id,name,private,remark,link,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"name\"\n		>\n		</page>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n  </seg>\n	\n</rule>', NULL, NULL, '2020-12-30 15:23:08', '2020-12-30 15:24:41');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (112, 49, 'api', '/menu/add', '新增菜单', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单新增\" corn=\"\">\n	<seg name=\"菜单新增\">\n		<parse name=\"解析参数\" var=\"vo\"/>\n      <bean name=\"bean\" key=\"vo\" parseBody=\"true\">\n        <field name=\"菜单名称\" key=\"name\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"上级菜单ID\" key=\"parentId\" mock=\"@integer(1, 10)\" reg=\"\" verify=\"required\" />\n        <field name=\"链接地址\" key=\"url\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"备注\" key=\"remark\" mock=\"@string(\'lower\', 10)\" reg=\"\" />\n      </bean>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n		\n		<exeSql name=\"插入数据\" var=\"insertCnt\" cols=\"id,vo.parentId,vo.name,vo.remark,vo.url,createTime,vo.upTime\"\n			sql=\"insert into menu(id,parentId,name,remark,url,createTime,upTime) values(?,?,?,?,?,?,?)\"\n		>\n			\n		</exeSql>\n		<if name=\"判断插入行数\" js=\"insertCnt>0\" true=\"插入成功\"  false=\"失败\"/>\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-12-30 17:16:56', '2020-12-30 17:27:01');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (113, 49, 'api', '/menu/mod', '菜单修改', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单修改\" corn=\"\">\n	<seg name=\"菜单修改\">\n      \n		<parse name=\"解析参数\" var=\"vo\" type=\"app\" />\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"id\" key=\"id\" mock=\"@integer(1, 10)\" reg=\"\"   verify=\"required\"/>\n        <field name=\"菜单名称\" key=\"name\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"上级菜单ID\" key=\"parentId\" mock=\"@integer(1, 10)\" reg=\"\" verify=\"required\" />\n        <field name=\"链接地址\" key=\"url\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"备注\" key=\"remark\" mock=\"@string(\'lower\', 10)\" reg=\"\" />\n      </bean>\n		<var name=\"设置修改时间\" var=\"upTime\" type=\"date\"/>\n		\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"vo.parentId,vo.name,vo.remark,vo.url,upTime,vo.id\"\n			sql=\"update menu set parentId=?,name=?,remark=?,url=?,upTime=? where id=?\"\n		>\n			\n		</exeSql>\n		<if name=\"判断更新行数\" js=\"cnt>0\" true=\"更新成功\"  false=\"失败\"/>\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2020-12-30 17:18:43', '2020-12-30 17:29:16');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (114, 49, 'api', '/menu/page', '菜单分页', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单查询\" corn=\"\">\n	<seg name=\"菜单查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"菜单名称\" var=\"name\" key=\"name\" />\n        <param name=\"上级菜单ID\" var=\"parentId\" key=\"parentId\" mock=\"0\"/>\n		<param name=\"菜单路径\" var=\"paths\" key=\"paths\" mock=\",0\"/>\n		<var name=\"sql\" var=\"sql\"   hasBody=\"true\">\n        <![CDATA[\n              SELECT * from (\n\nSELECT * FROM (\n     SELECT id,name,remark,url,parentId,\n     @le:= IF (parentId = 0 ,0,  \n         IF( LOCATE( CONCAT(\'|\',parentId,\':\'),@pathlevel)   > 0  ,      \n                  SUBSTRING_INDEX( SUBSTRING_INDEX(@pathlevel,CONCAT(\'|\',parentId,\':\'),-1),\'|\',1) +1\n        ,@le+1) ) levels\n     , @pathlevel:= CONCAT(@pathlevel,\'|\',id,\':\', @le ,\'|\') pathlevel\n      , @pathnodes:= IF( parentId =0,\',0\', \n           CONCAT_WS(\',\',\n           IF( LOCATE( CONCAT(\'|\',parentId,\':\'),@pathall) > 0  , \n               SUBSTRING_INDEX( SUBSTRING_INDEX(@pathall,CONCAT(\'|\',parentId,\':\'),-1),\'|\',1)\n              ,@pathnodes ) ,parentId  ) )paths\n    ,@pathall:=CONCAT(@pathall,\'|\',id,\':\', @pathnodes ,\'|\') pathall \n        FROM  menu, \n    (SELECT @le:=0,@pathlevel:=\'\', @pathall:=\'\',@pathnodes:=\'\') vv\n    ORDER BY  parentId,id\n    ) src   \n	\n)t where 1=1\n            ]]>\n        </var>\n        \n		<append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n        <append name=\"添加parentId\" key=\"sql\" when=\"parentId\" value=\" and parentId=? \"/>\n        <append name=\"添加路径\" key=\"sql\" when=\"paths\" value=\" and paths like concat(?,\'%\') \"/>\n        <print name=\"test\" var=\"sql\" />\n		<page name=\"查询数据\" var=\"list\" cols=\"id,parentId,name,remark,url,levels,paths,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"name,parentId,paths\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2020-12-30 17:19:17', '2021-1-2 11:11:23');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (115, 49, 'api', '/menu/get', '根据ID查询应用菜单', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"查询\" corn=\"\">\n	<seg name=\"查询\">\n		<param name=\"资源ID\" var=\"id\" key=\"id\" />\n		<sqlFind name=\"查询数据\" var=\"list\" cols=\"id,parentId,name,remark,url,createTime,upTime\" param=\"id\"\n		sql=\"select * from menu where 1=1 and id=?\" \n		>\n			\n		</sqlFind>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2020-12-30 17:19:58', '2020-12-30 17:32:07');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (116, 49, 'api', '/menu/del', '删除菜单', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单删除\" corn=\"\">\n	<seg name=\"菜单删除\">\n		<param name=\"id\" var=\"id\" key=\"id\"/>\n		\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"id\"\n			sql=\"delete from  menu where id=?\"\n		>\n			\n		</exeSql>\n		<if name=\"判断更新行数\" js=\"cnt>0\" true=\"更新成功\"  false=\"失败\"/>\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2020-12-30 17:20:26', '2020-12-30 17:32:51');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (117, 49, 'api', '/menu/role/menus', '根据角色查询菜单列表', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"查询\" corn=\"\">\n	<seg name=\"查询\">\n		<param name=\"角色ID\" var=\"roleId\" key=\"roleId\" />\n		<sqlFind name=\"查询数据\" var=\"list\" cols=\"id,roleId,menuId\" param=\"roleId\"\n		sql=\"select * from role_menu_ref where 1=1 and roleId=?\" \n		>\n			\n		</sqlFind>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-1-1 19:30:06', '2021-1-1 19:33:44');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (118, 49, 'api', '/menu/bind/role', '角色分配菜单', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单分配菜单\" corn=\"\">\n	<seg name=\"菜单分配菜单\">\n		<parse name=\"解析参数\" var=\"vo\" />\n        <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n            <field name=\"roleId\" key=\"roleId\" mock=\"@integer(1, 10)\" reg=\"\"   verify=\"required\"/>\n            <field name=\"menuIds\" key=\"menuIds\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        </bean>\n		\n        <split name=\"拆分菜单集合\" var=\"menuIdList\" :key=\"#{vo.menuIds}\" split=\",\"/>\n        <exeSql name=\"删除关联角色菜单\" var=\"insertCnt\" cols=\"vo.roleId\"\n			sql=\"delete from role_menu_ref where roleId=?\"\n		>\n		</exeSql>\n        <count name=\"数据索引位置\" init=\"0\"  var=\"index\"/>\n        <go name=\"跳转循环新增菜单\" to=\"循环新增菜单\"/>\n	</seg>\n    <seg name=\"循环新增菜单\">\n        <var name=\"获取menuIdList\"  key=\"menuIdList\" :index=\"#{index}\"  var=\"menuId\"  />\n        <if name=\"判断menuId是否存在\" js=\"menuId!=\'\'&amp;&amp; menuId!=null \" false=\"跳转到结束\" />\n        <exeSql name=\"插入标签数据\" var=\"insertCnt1\" cols=\"menuId,vo.roleId\"\n                sql=\"insert into role_menu_ref(menuId,roleId) values(?,?)\"\n            >\n            </exeSql>\n        <count name=\"数据索引位置+1\"  inc=\"1\" var=\"index\"/>\n        \n        <go name=\"跳转循环新增菜单\" to=\"循环新增菜单\"/>\n        <go name=\"跳转到结束\" to=\"结束\"/>\n    </seg>\n    <seg name=\"结束\">\n        <resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n        <go  name=\"跳转到结束\" to=\"end\"/>\n        <resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n        <empty name=\"end\"/>\n    </seg>\n	\n</rule>', NULL, NULL, '2021-1-2 11:54:35', '2021-1-2 12:06:33');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (119, 49, 'api', '/menu/my', '我的菜单', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"菜单查询\" corn=\"\">\n	<seg name=\"菜单查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<user name=\"当前用户\" var=\"user\" />\n        <var name=\"userId\" key=\"user\" childKey=\"id\" var=\"userId\" />\n		<var name=\"sql\" var=\"sql\"   hasBody=\"true\">\n        <![CDATA[\n             SELECT * FROM menu where 1=1 and id in(\n		SELECT menuId FROM role_menu_ref WHERE roleId in(\n		SELECT roleId from user_role_ref WHERE userId=?\n	)\n)\n            ]]>\n        </var>\n        \n        \n		<page name=\"查询数据\" var=\"list\" cols=\"id,parentId,name,remark,url,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"userId\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-1-2 20:58:50', '2021-1-2 21:23:03');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (120, 2, 'api', '/cmd/exe', '执行命令', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"执行命令\" corn=\"\">\n	<seg name=\"执行命令\">\n		<param name=\"cmd\" var=\"cmd\" key=\"cmd\" mock=\"@string(\'lower\', 5)\"/>\n        <split name=\"拆分命令集合\" var=\"cmds\" :key=\"#{cmd}\" split=\",\"/>\n        \n		<shell name=\"执行命令\" cmd=\"cmds\" var=\"rs\" />\n		<resp name=\"创建成功\" code=\"200\" :msg=\"查询成功\" key=\"rs\"/>\n        <print name=\"test1\" var=\"rs\" />\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-13 10:17:50', '2021-1-13 12:07:16');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (121, 3, 'plugin', 'changeTabCmd.java', '切换选项卡', NULL, 'package com.exe.web.driver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport kkd.common.util.StringUtil;\nimport org.openqa.selenium.WebDriver;\n\nimport java.util.Map;\nimport java.util.Set;\n\npublic class ChangeTabCmd extends Cmd{\n	\n	@Override\n	public String op() {\n		return \"changeTab\";\n	}\n\n	/**\n	 * 切换选项卡\n	 * @param param\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		Map<String, String> tabIdMap= (Map<String, String>) sys.get(\"tabIdMap\");\n		String target=param.get(\"target\");\n		Set<String> windowHandles = driver.getWindowHandles();\n		for(String handle:windowHandles) {\n			String tmpTarget=tabIdMap.get(handle);\n			if(StringUtil.equalsIgnoreCase(target, tmpTarget)){\n				System.out.println(\"找到tab 并切换成功:\"+target);\n				driver.switchTo().window(handle);\n				return;\n			}\n		}\n		System.out.println(\"没有找到tab:\"+target);\n		//新打开页面的定位方法\n//		String parentHandle=driver.getWindowHandle();\n//		System.out.println(\"parent的句柄：\"+parentHandle);\n//		//获取所有的Handle\n//		Set<String> windowHandles = driver.getWindowHandles();\n//		System.out.println(\"所有句柄：\"+windowHandles);\n//		//判断是否打开了新窗口\n//		for(String handle:windowHandles) {\n//			System.out.println(\"页面的句柄：\"+handle);\n//			if (!parentHandle.equals(handle)) {\n//				driver.switchTo().window(handle);\n//			}\n//		}		\n	}\n\n	\n}\n', NULL, NULL, '2021-1-15 19:32:13', '2021-1-15 19:32:37');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (122, 3, 'plugin', 'checkCmd.java', '元素检查', NULL, 'package com.exe.web.driver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.WebElement;\n\nimport java.util.Map;\n\npublic class CheckCmd extends Cmd {\n	@Override\n	public String op() {\n		return \"check\";\n	}\n	\n	/**\n	 * 根据元素是否存在 跳转\n	 * @param param\n	 * @return\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		String byType = param.get(\"byType\");\n		String byVal = param.get(\"byVal\");\n		String trueVal = param.get(\"true\");\n		String falseVal = param.get(\"false\");\n		WebElement web = DriverUtil.getWebElement(driver, byType, byVal);\n		if (web != null) {\n			cmdHandle.gotoNodeByName(caiAction,trueVal);\n		} else {\n			cmdHandle.gotoNodeByName(caiAction,falseVal);\n		}\n	}\n\n	\n\n}\n', NULL, NULL, '2021-1-15 19:33:15', '2021-1-15 19:33:28');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (123, 3, 'plugin', 'checklenCmd.java', '元素内容长度检查', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport kkd.common.util.StringUtil;\n\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.WebElement;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class ChecklenCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"checklen\";\n	}\n	/**\n	 * attr 取值 属性取值  默认 text\n	 * @param param\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		String byType=param.get(\"byType\");\n		String byVal=param.get(\"byVal\");\n		String attr=param.get(\"attr\");\n		Integer minLen=Integer.valueOf(param.get(\"minLen\"));\n		String trueVal=param.get(\"true\");\n		String falseVal=param.get(\"false\");\n		WebElement web=DriverUtil.getWebElement(driver,byType, byVal);\n		if(web!=null){\n			String data=web.getText();\n			if(!StringUtil.isEmpty(attr)){\n				data=web.getAttribute(attr);\n			}\n			if(data.length()>=minLen){\n				cmdHandle.gotoNodeByName(caiAction,trueVal);\n			}else{\n				cmdHandle.gotoNodeByName(caiAction,falseVal);\n			}\n		}else{\n			cmdHandle.gotoNodeByName(caiAction,falseVal);\n		}\n	}\n	\n	\n}\n', NULL, NULL, '2021-1-15 19:34:06', '2021-1-15 20:37:03');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (124, 3, 'plugin', 'checkvalueCmd.java', '元素值检查', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport kkd.common.util.StringUtil;\n\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.WebElement;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class CheckvalueCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"checkvalue\";\n	}\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		String byType=param.get(\"byType\");\n		String byVal=param.get(\"byVal\");\n		String attr=param.get(\"attr\");\n		String c=param.get(\"c\");\n		String value=param.get(\"value\");\n		\n		String trueVal=param.get(\"true\");\n		String falseVal=param.get(\"false\");\n		WebElement web=DriverUtil.getWebElement(driver,byType, byVal);\n		if(web!=null){\n			String data=web.getText();\n			if(!StringUtil.isEmpty(attr)){\n				data=web.getAttribute(attr);\n			}\n			if(\"eq\".equalsIgnoreCase(c)){\n				if(StringUtil.equalsIgnoreCase(data, value)){\n					cmdHandle.gotoNodeByName(caiAction,trueVal);\n				}else{\n					cmdHandle.gotoNodeByName(caiAction,falseVal);\n				}\n			}else if(\"like\".equalsIgnoreCase(c)){\n				if(data.indexOf(value)!=-1){\n					cmdHandle.gotoNodeByName(caiAction,trueVal);\n				}else{\n					cmdHandle.gotoNodeByName(caiAction,falseVal);\n				}\n			}\n		}else{\n			cmdHandle.gotoNodeByName(caiAction,falseVal);\n		}		\n	}\n\n}\n', NULL, NULL, '2021-1-15 19:34:47', '2021-1-15 19:35:02');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (125, 3, 'plugin', 'clickCmd.java', '元素点击', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.WebElement;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class ClickCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"click\";\n	}\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		String byType=param.get(\"byType\");\n		String byVal=param.get(\"byVal\");\n		WebElement web=DriverUtil.getWebElement(driver,byType, byVal);\n//		String code=getElementByJs(byType,byVal);\n//		if(code!=null){\n//			code=code+\"tmp1111.scrollIntoView(true)\";\n//			js.executeScript(code);\n//		}\n		if(web==null){\n			throw new RuntimeException(\"未找到点击元素：\"+byVal);\n		}\n		web.click();\n	}\n}\n', NULL, NULL, '2021-1-15 19:35:36', '2021-1-15 19:36:21');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (126, 3, 'plugin', 'closeTabCmd.java', '关闭选项卡', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\nimport java.util.Set;\n\nimport org.openqa.selenium.WebDriver;\n\nimport kkd.common.util.StringUtil;\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class CloseTabCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"closeTab\";\n	}\n	\n	/**\n	 * 关闭 target选项卡\n	 * @param param\n	 */\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		Map<String, String> tabIdMap= (Map<String, String>) sys.get(\"tabIdMap\");\n		Map<String, String> tabMap= (Map<String, String>) sys.get(\"tabMap\");\n		\n		String target=param.get(\"target\");\n		if(!StringUtil.isEmpty(target)){\n			Set<String> windowHandles = driver.getWindowHandles();\n			for(String handle:windowHandles) {\n				String tmpTarget=tabIdMap.get(handle);\n				if(StringUtil.equalsIgnoreCase(target, tmpTarget)){\n					driver.switchTo().window(handle);\n				}\n			}\n		}\n		\n		\n		String tabid=driver.getWindowHandle();\n//		String target=tabIdMap.get(tabid);\n		//移除tab管理数据\n		tabIdMap.remove(tabid);\n		tabMap.remove(target);\n		driver.close();		\n	}\n	\n}\n', NULL, NULL, '2021-1-15 19:36:55', '2021-1-15 19:38:47');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (127, 3, 'plugin', 'inputCmd.java', '文本输入', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.WebElement;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class InputCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"input\";\n	}\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		String byType=param.get(\"byType\");\n		String byVal=param.get(\"byVal\");\n		String data=param.get(\"value\");\n		\n		WebElement web = DriverUtil.getWebElement(driver,byType, byVal);\n		web.sendKeys(data);		\n		\n	}\n	\n	\n\n}\n', NULL, NULL, '2021-1-15 19:37:48', '2021-1-15 19:39:10');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (128, 3, 'plugin', 'loadCookieLocalCmd.java', 'cookie加载', NULL, 'package com.exe.web.driver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport org.openqa.selenium.WebDriver;\n\nimport java.util.Map;\n\npublic class LoadCookieLocalCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"loadCookieLocal\";\n	}\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		CookieUtil.loadCookieLocal(driver);			\n	}\n\n}\n', NULL, NULL, '2021-1-15 19:38:25', '2021-1-15 19:39:37');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (129, 3, 'plugin', 'openCmd.java', '打开网页', NULL, 'package com.exe.web.driver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport kkd.common.util.StringUtil;\nimport org.openqa.selenium.JavascriptExecutor;\nimport org.openqa.selenium.WebDriver;\nimport org.openqa.selenium.chrome.ChromeDriver;\nimport org.openqa.selenium.chrome.ChromeOptions;\n\nimport java.util.Arrays;\nimport java.util.HashMap;\nimport java.util.Map;\nimport java.util.Set;\n\npublic class OpenCmd extends Cmd{\n	\n	@Override\n	public String op() {\n		return \"open\";\n	}\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		if(driver==null){\n			System.setProperty(\"webdriver.chrome.driver\",\n					\"/usr/bin/chromedriver\");\n\n\n			ChromeOptions opt = new ChromeOptions();\n			opt.addArguments(\"disable-infobars\");\n			opt.addArguments(\"--no-sandbox\");\n			opt.addArguments(\"--headless\");\n			opt.addArguments(\"--disable-gpu\");\n			opt.addArguments(\"--disable-dev-shm-usage\");\n			opt.setExperimentalOption(\"excludeSwitches\",\n					Arrays.asList(\"enable-automation\"));\n			driver = new ChromeDriver(opt);\n			JavascriptExecutor js = (JavascriptExecutor) driver;\n			sys.put(\"driver\",driver);\n			sys.put(\"js\",js);\n		}\n\n		Map<String, Object> vars=cmdHandle.getVars();\n		JavascriptExecutor js= (JavascriptExecutor) sys.get(\"js\");\n		Map<String, String> tabIdMap= (Map<String, String>) sys.get(\"tabIdMap\");\n		Map<String, String> tabMap= (Map<String, String>) sys.get(\"tabMap\");\n		String url=param.get(\"url\");\n		String target=param.get(\"target\");\n		String var=param.get(\"var\");\n		//设置环境变量\n		if(!StringUtil.isEmpty(var)&&!StringUtil.isEmpty(url)){\n			vars.put(var, url);\n		}\n		if(StringUtil.isEmpty(url)){\n			url=param.get(\"defaultUrl\");\n		}\n		if(tabMap==null||tabMap.size()==0){\n			tabMap=new HashMap<String, String>();\n			tabIdMap=new HashMap<String, String>();\n			sys.put(\"tabIdMap\",tabIdMap);\n			sys.put(\"tabMap\",tabMap);\n			String tabId=driver.getWindowHandle();\n			if(StringUtil.isEmpty(target)){\n				target=tabId;\n			}\n			tabMap.put(target, tabId);\n			tabIdMap.put(tabId, target);\n		}\n		\n		if(StringUtil.isEmpty(target)){\n			js.executeScript(\"window.open(\\\"\"+url+\"\\\");\");\n			Set<String> allTabs=driver.getWindowHandles();\n			for (String tmpTabId : allTabs) {\n				//查找 没有被管理的tab \n				if(tabIdMap.get(tmpTabId)==null){\n					target=tmpTabId;\n					tabIdMap.put(tmpTabId, target);\n					tabMap.put(target, tmpTabId);\n					//切换到改选项卡\n					driver.switchTo().window(tmpTabId);\n					driver.get(url);\n					return ;\n					//driver.get(url);\n				}\n			}\n			\n		}else{\n			String tabId=tabMap.get(target);\n			if(tabId==null){\n//				Actions newTab = new Actions(driver);\n				//浏览器打开标签页的快捷键是ctrl+t，那把ctrl+t的按键事件传入即可\n				//newTab.keyDown(Keys.CONTROL).sendKeys(\"t\").keyUp(Keys.CONTROL).perform();\n				//driver.findElement(By.cssSelector(\"body\")).sendKeys(Keys.CONTROL +\"t\");\n//				js.executeScript(\"window.open(\\\"chrome://version\\\");\");\n				js.executeScript(\"window.open(\\\"\"+url+\"\\\");\");\n				Set<String> allTabs=driver.getWindowHandles();\n				for (String tmpTabId : allTabs) {\n					//查找 没有被管理的tab \n					if(tabIdMap.get(tmpTabId)==null){\n						tabIdMap.put(tmpTabId, target);\n						tabMap.put(target, tmpTabId);\n						//切换到改选项卡\n						driver.switchTo().window(tmpTabId);\n						return ;\n						//driver.get(url);\n					}\n				}\n				throw new RuntimeException(\"打开选项卡失败\");\n			}else{\n				if(StringUtil.isEmpty(target)){\n					target=tabId;\n				}\n				driver.switchTo().window(tabId);\n				driver.get(url);\n			}\n		}		\n	}\n	\n}\n', NULL, NULL, '2021-1-15 19:40:16', '2021-1-15 20:58:42');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (130, 3, 'plugin', 'quitCmd.java', '退出网页', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport org.openqa.selenium.JavascriptExecutor;\nimport org.openqa.selenium.WebDriver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class QuitCmd extends Cmd{\n	\n	@Override\n	public String op() {\n		return \"quit\";\n	}\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		Map<String, Object> vars=cmdHandle.getVars();\n		driver.quit();\n	}\n\n}\n', NULL, NULL, '2021-1-15 19:40:40', '2021-1-15 19:41:53');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (131, 3, 'plugin', 'returnCmd.java', '网页返回', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class ReturnCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"return\";\n	}\n	\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		cmdHandle.returnCmd();\n	}\n\n}\n', NULL, NULL, '2021-1-15 19:41:11', '2021-1-15 19:42:06');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (132, 3, 'plugin', 'saveCookieCmd.java', '保存cookie', NULL, 'package com.exe.web.driver;\n\nimport java.util.Map;\n\nimport org.openqa.selenium.JavascriptExecutor;\nimport org.openqa.selenium.WebDriver;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\npublic class SaveCookieCmd extends Cmd{\n	@Override\n	public String op() {\n		return \"saveCookie\";\n	}\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n		WebDriver driver= (WebDriver) sys.get(\"driver\");\n		CookieUtil.saveCookie(driver);\n	}\n\n}\n', NULL, NULL, '2021-1-15 19:41:34', '2021-1-15 19:42:17');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (133, 50, 'api', '/task/add', '任务新增', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务新增\" corn=\"\">\n	<seg name=\"任务新增\">\n		<parse name=\"解析参数\" var=\"vo\"/>\n      <bean name=\"bean\" key=\"vo\" parseBody=\"true\">\n        <field name=\"任务名称\" key=\"job_name\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"任务描述\" key=\"description\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n        <field name=\"CRON表达式\" key=\"cron_expression\" mock=\"@string(\'lower\', 5)\" reg=\"\"  verify=\"required\" />\n        <field name=\"类名\" key=\"bean_class\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\"  />\n        <field name=\"参数\" key=\"params\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n        <field name=\"任务状态\" key=\"job_status\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        <field name=\"任务分组\" key=\"job_group\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n		<var name=\"设置当前时间\" var=\"createTime\" type=\"date\"/>\n		\n		<exeSql name=\"插入数据\" var=\"insertCnt\" cols=\"id,vo.job_name,vo.description,vo.cron_expression,vo.bean_class,vo.params,vo.job_status,vo.job_group,createTime,vo.upTime\"\n			sql=\"insert into sys_task(id,job_name,description,cron_expression,bean_class,params,job_status,job_group,createTime,upTime) values(?,?,?,?,?,?,?,?,?,?)\"\n		>\n			\n		</exeSql>\n		<if name=\"判断插入行数\" js=\"insertCnt>0\" true=\"插入成功\"  false=\"失败\"/>\n		<resp name=\"插入成功\" code=\"200\" msg=\"插入成功\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-17 11:12:50', '2021-1-17 23:29:09');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (134, 50, 'api', '/task/mod', '任务修改', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务修改\" corn=\"\">\n	<seg name=\"任务修改\">\n		<parse name=\"解析参数\" var=\"vo\" type=\"app\" />\n        <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n            <field name=\"id\" key=\"id\" mock=\"@integer(1, 10)\" reg=\"\"   verify=\"required\"/>\n            <field name=\"任务名称\" key=\"job_name\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n            <field name=\"任务描述\" key=\"description\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n            <field name=\"CRON表达式\" key=\"cron_expression\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\"  />\n            <field name=\"类名\" key=\"bean_class\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\"  />\n            <field name=\"参数\" key=\"params\" mock=\"@string(\'lower\', 5)\" reg=\"\"  />\n            <field name=\"任务状态\" key=\"job_status\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n            <field name=\"任务分组\" key=\"job_group\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n        </bean>\n		<var name=\"设置修改时间\" var=\"upTime\" type=\"date\"/>\n		\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"vo.job_name,vo.description,vo.cron_expression,vo.bean_class,vo.params,vo.job_status,vo.job_group,upTime,vo.id\"\n			sql=\"update sys_task set job_name=?,description=?,cron_expression=?,bean_class=?,params=?,job_status=?,job_group=?,upTime=? where id=?\"\n		>\n			\n		</exeSql>\n		<if name=\"判断更新行数\" js=\"cnt>0\" true=\"更新成功\"  false=\"失败\"/>\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-17 11:13:19', '2021-1-17 23:29:00');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (135, 50, 'api', '/task/page', '任务分页', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务查询\" corn=\"\">\n	<seg name=\"任务查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"任务名称\" var=\"job_name\" key=\"job_name\" />\n		\n		<var name=\"sql\" var=\"sql\" init=\"select * from sys_task where 1=1 \"/>\n		<append name=\"添加job_name\" key=\"sql\" when=\"job_name\" value=\" and job_name like concat(\'%\',?,\'%\') \"/>\n		<page name=\"查询数据\" var=\"list\" cols=\"id,job_name,description,cron_expression,bean_class,params,job_status,job_group,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"job_name\"\n		>\n			\n		</page>\n		<code name=\"获取任务状态\" hasBody=\"true\">\n            <![CDATA[\n                def exe(a,cmdHandle){\n                    def list=cmdHandle.vars.list\n                    if(list==null|| list.collection==null){\n                        return;\n                    }\n                    def dataList=list.collection\n                    for(int i=0;i<dataList.size();i++){\n                        def item=dataList.get(i)\n                        item.job_status=item.job_status+\"-\"+getState(item)\n                    }\n                }\n                def getState(item){\n                    try{\n                        def qm = com.exe.web.task.QuartzManager.get();\n                        def task = new com.exe.web.task.TaskDO();\n                        //task.setBeanClass(\"com.exe.web.task.TestJob\");\n                        \n                        task.setJobGroup(item.job_group);\n                        task.setJobName(item.job_name);\n                        String state=qm.getState(task);\n                        return state;\n                    }catch (Exception e){\n                        e.printStackTrace();\n                    }\n                    return null;\n                }\n            ]]>\n        </code>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-1-17 11:13:36', '2021-1-19 21:39:40');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (136, 50, 'api', '/task/get', '根据ID查询任务', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"查询\" corn=\"\">\n	<seg name=\"查询\">\n		<param name=\"ID\" var=\"id\" key=\"id\" />\n		\n		<sqlFind name=\"查询数据\" var=\"list\" cols=\"id,job_name,description,cron_expression,bean_class,params,job_status,job_group,createTime,upTime\" param=\"id\"\n		sql=\"select * from sys_task where 1=1 and id=?\" \n		>\n			\n		</sqlFind>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-17 20:37:22', '2021-1-17 23:29:40');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (137, 50, 'api', '/task/del', '任务删除', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务删除\" corn=\"\">\n	<seg name=\"任务删除\">\n		<param name=\"id\" var=\"id\" key=\"id\"/>\n		\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"id\"\n			sql=\"delete from  sys_task where id=?\"\n		>\n			\n		</exeSql>\n		<if name=\"判断更新行数\" js=\"cnt>0\" true=\"更新成功\"  false=\"失败\"/>\n		<resp name=\"更新成功\" code=\"200\" :msg=\"更新成功，id:#{vo.id}\" data=\"\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-17 20:37:51', '2021-1-17 20:51:47');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (138, 50, 'api', '/task/start', '启动任务', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务启动\" corn=\"\">\n	<seg name=\"任务查询\">\n		<param name=\"ID\" var=\"id\" key=\"id\" />\n        <sqlFind name=\"查询数据\" var=\"list\" cols=\"id,job_name,description,cron_expression,bean_class,params,job_status,job_group,createTime,upTime\" param=\"id\"\n		sql=\"select * from sys_task where 1=1 and id=?\" \n		>\n			\n		</sqlFind>\n		<code name=\"addJob\" hasBody=\"true\">\n        <![CDATA[\n          def exe(a,cmdHandle){\n                def list=cmdHandle.getVars().get(\"list\");\n                def rs=\"\";\n                if(list== null ||list.size==0){\n                    rs=\"list is null\"\n                }\n                def taskMap=list.get(0)\n          		def qm=com.exe.web.task.QuartzManager.get()\n                def task=new com.exe.web.task.TaskDO();\n                task.setBeanClass(taskMap.get(\"bean_class\"));\n                task.setCronExpression(taskMap.get(\"cron_expression\"));\n                task.setJobGroup(taskMap.get(\"job_group\"));\n                task.setJobName(taskMap.get(\"job_name\"));\n                task.setId(taskMap.get(\"id\"));\n                task.setParams(taskMap.get(\"params\"));\n                rs=qm.addJob(task);\n                \n          		cmdHandle.getVars().put(\"rs\",rs)\n          }\n        ]]>\n        </code>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"启动成功\" key=\"rs\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-1-17 21:25:42', '2021-1-19 21:33:44');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (139, 50, 'api', '/task/stop', '停止任务', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"任务启动\" corn=\"\">\n	<seg name=\"任务查询\">\n		<param name=\"ID\" var=\"id\" key=\"id\" />\n        <sqlFind name=\"查询数据\" var=\"list\" cols=\"id,job_name,description,cron_expression,bean_class,job_status,job_group,createTime,upTime\" param=\"id\"\n		sql=\"select * from sys_task where 1=1 and id=?\" \n		>\n			\n		</sqlFind>\n		<code name=\"deleteJob\" hasBody=\"true\">\n        <![CDATA[\n          def exe(a,cmdHandle){\n                def list=cmdHandle.getVars().get(\"list\");\n                def rs=\"\";\n                if(list== null ||list.size==0){\n                    rs=\"list is null\"\n                }\n                def taskMap=list.get(0)\n          		def qm=com.exe.web.task.QuartzManager.get()\n                def task=new com.exe.web.task.TaskDO();\n                task.setBeanClass(taskMap.get(\"bean_class\"));\n                task.setCronExpression(taskMap.get(\"cron_expression\"));\n                task.setJobGroup(taskMap.get(\"job_group\"));\n                task.setJobName(taskMap.get(\"job_name\"));\n                rs=qm.deleteJob(task);\n          		cmdHandle.getVars().put(\"rs\",rs)\n          }\n        ]]>\n        </code>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"停止成功\" key=\"rs\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-1-17 21:26:09', '2021-1-19 15:32:13');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (141, 30, 'api', '/test/demo1', '测试', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"跑单任务\" corn=\"1\">\n	<seg name=\"跑单任务\">\n        \n        <code name=\"解析数据\" hasBody=\"true\">\n        <![CDATA[\n            def exe(a,cmdHandle){\n               def lines=getLine();\n               def map=parseData(line);\n               def userId=map.get(\"userId\")\n               def token=map.get(\"token\")\n               def user_type=map.get(\"user_type\")\n               def product_id=map.get(\"product_id\")\n               def description=map.get(\"description\")\n               if(inBlack(userId)){\n                   println \"$userId 黑名单中\"\n                   return ;\n               }\n               if(overLimit(userId)){\n                   println \"$userId 超过限制\"\n                   return ;\n               }\n                cmdHandle.vars.put(\"map\",map)\n                println \"start 跑单任务 $map\"\n          }\n          def getLine(){\n                def root=com.exe.web.util.MyConfUtil.getRootPath()\n                def path=root+\"/static/logs/info/\"\n                def dateStr=kkd.common.util.DateUtil.format(\"yyyy-MM-dd\")\n                path=path+dateStr+\"/order-log.log\"\n                java.util.List<String> list=new java.util.ArrayList<>();\n                kkd.common.util.FileUtil.readFileByLines(\"\", new kkd.common.util.FileUtil.FileUtilReader() {\n                    @Override\n                    public void read(Object o) {\n                        println \"read line:$o\"\n                        list.add(o.toString());\n                    }\n                });\n              return list\n          }\n            private  java.util.Map<String,String> parseData(String line){\n                java.util.Map<String,String> map=null;\n                String start=\"----start-----\";\n                int startLen=start.length();\n                int startPos=line.indexOf(start);\n                if(startPos!=-1){\n                    String end=\"----start---end--\";\n                    int endPos=line.indexOf(end,startPos+1);\n                    String dataStr=line.substring(startPos+startLen,endPos);\n                    map=kkd.common.util.StringUtil.getParaCollection(dataStr,\"&\",\"=\");\n                }\n                return map;\n            }\n          \n          /**\n          是否在黑名单\n          */\n          def inBlack(userId){\n                def dh=kkd.common.dao.dbuitl.JDBC.getDefaultNoTransactionDbHelper();\n                java.util.List<kkd.common.dao.dbuitl.SqlParameter> parameters=null;\n                try{\n                    def uid=dh.executeQueryOne(\"select user_id from iptv_order_user where user_id=\'$userId\' and user_type=\'12\'\",parameters,String.class);\n                    if(uid!=null){\n                        return true;\n                    }\n                }catch (Exception e){\n                    e.printStackTrace();\n                }\n              return false\n          }\n           /**\n          是否超过限制\n          */\n          def overLimit(userId){\n              return true\n          }\n         ]]>\n        </code>\n        <open name=\"open\" url=\"http://www.baidu.com\" target=\"test\"/>\n        <code name=\"跑单\" hasBody=\"true\">\n        <![CDATA[\n            def exe(a,cmdHandle){\n                Map<String, Object> sys=cmdHandle.getSys();\n		        def driver= sys.get(\"driver\");\n                def title=driver.title;\n                println \"---------title---$title\"\n                def map=cmdHandle.vars.map\n                \n            }\n            /**\n          发送请求\n          */\n            def send(){\n                String url=\"\";\n                def params=new java.util.HashMap<>();\n                params.put(\"userId\",userId);\n                params.put(\"token\",token);\n                def rs=HttpUtil.get(url,params,15,false);\n                if(rs.indexOf(\"\")!=-1){\n                    //放入黑名单\n                    putBlack(userId);\n                }\n            \n            }\n            /**\n          放入黑名单\n          */\n          def putBlack(userId,user_type,product_id,description){\n                def create_date=new java.util.Date()\n               \n                def dh=kkd.common.dao.dbuitl.JDBC.getDefaultNoTransactionDbHelper();\n                java.util.List<kkd.common.dao.dbuitl.SqlParameter> parameters=null;\n                try{\n                    String sql=\"insert into iptv_order_user(user_id,user_type,product_id,create_date,description)values\";\n                    sql=sql+\"(\'$user_id\',\'$user_type\',\'$product_id\',\'$create_date\',\'$description\')\";\n                    boolean success=dh.executeSql(sql);\n                    if(success){\n                        return true;\n                    }\n                }catch (Exception e){\n                    e.printStackTrace();\n                }\n                return false\n          }\n         ]]>\n        </code>\n        <shell name=\"执行命令\" var=\"rs\" cmd=\"echo hello\" />\n		<resp name=\"成功\" code=\"200\" msg=\"成功\" key=\"rs\" />\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-18 10:57:22', '2021-1-18 13:05:17');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (142, 2, 'filter', '/api/menu/*', '用户登录过滤器', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"登录过滤\" corn=\"\">\n	<seg name=\"登录过滤\">\n        <cookie name=\"获取cookie\" key=\"sessionId\" op=\"get\" var=\"sessionId\"/>\n        <if name=\"cookie是否存在\"  js=\"sessionId!=null\"   false=\"end\" />\n        <!-- 获取用户会话 -->\n		<session name=\"获取用户会话\" :key=\"#{sessionId}\" upDate=\"true\" op=\"get\" var=\"user\"/>\n        <if name=\"用户是否存在\"  js=\"user!=null\"   false=\"end\" />\n		<filter name=\"过滤器1\" return=\"false\" />\n		<go name=\"跳转到结束\" to=\"success\"/>\n        <empty name=\"end\"/>\n        <resp name=\"未登录\" code=\"1\" msg=\"未登录\" data=\"\"/>\n        <filter name=\"过滤器2\" return=\"true\" />\n        <empty name=\"success\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-1-18 13:35:51', '2021-1-18 13:36:01');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (145, 32, 'api', '/scj/sc/my', '我的收藏', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"公开收藏查询\" corn=\"\">\n	<seg name=\"公开收藏查询\">\n      <param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n      <param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n      <param name=\"收藏夹名称\" var=\"name\" key=\"name\" />\n      <param name=\"标签ID\" var=\"tagIds\" key=\"tagIds\" />\n	  <user name=\"当前用户\" var=\"user\" />\n      <var name=\"userId\" key=\"user\" childKey=\"id\" var=\"userId\" />\n      <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1 and (private=\'on\' ) and uid=? \"/>\n      <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n      <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIds\" :value=\" and id in(	select scId from scj_tag_sc_ref where tagId in (#{tagIds}) )\"/>\n      \n		\n		<go name=\"跳转到结束\" to=\"结束\"/>\n	</seg>\n  <seg name=\"循环append标签1\">\n    <split name=\"拆分标签集合\" var=\"tagIdArr\" :key=\"#{tagIds}\" split=\",\"/>\n    <var name=\"sql\" var=\"sql\" init=\"select * from scj_sc where 1=1  \"/>\n    <append name=\"添加name\" key=\"sql\" when=\"name\" value=\" and name like concat(\'%\',?,\'%\') \"/>\n    <append name=\"添加tagIds1\" key=\"sql\" when=\"tagIdArr\" value=\" and id in(	select scId from scj_tag_sc_ref where tagId in\"/>\n    <code name=\"sql标签id拼接\" hasBody=\"true\">\n      def exe(cmd,cmdHandle){\n        def tagIds=cmdHandle.vars.tagIdArr\n        def oldSql=cmdHandle.vars.sql\n        def sql=\"(\"\n        def first=true\n        \n        for(tagId:tagIds){\n          if(first){\n            sql=sql+tagId\n              first=false\n          }else{\n         	 sql=sql+\",\"+tagId\n          }\n        }\n        sql=oldSql+sql+\")\"\n        print sql\n        cmdHandle.vars.put(\"sql\",sql)\n      }\n    </code>\n    <append name=\"添加右括号\" key=\"sql\" when=\"tagIdArr\" value=\" )\"/>\n    <go name=\"跳转到结束\" to=\"结束\"/>\n  </seg>\n  <seg name=\"结束1\">\n		<empty name=\"end\"/>\n  </seg>\n  <seg name=\"结束\">\n    <page name=\"查询数据\" var=\"list\" cols=\"id,name,private,remark,link,createTime,upTime\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"userId,name\"\n		>\n		</page>\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n  </seg>\n	\n</rule>', NULL, NULL, '2021-1-18 14:07:42', '2021-1-18 14:13:31');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (146, 47, 'api', '/user/mod/pwd', '修改密码', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"修改密码\" corn=\"\">\n	<seg name=\"修改密码\">\n		<parse name=\"解析参数\" var=\"vo\" />\n      <bean name=\"bean\"  key=\"vo\" parseBody=\"true\">\n        <field name=\"密码\" key=\"pwd\" mock=\"@string(\'lower\', 5)\" reg=\"\" verify=\"required\" />\n      </bean>\n        <user name=\"当前用户\" var=\"user\" />\n        <var name=\"userId\" key=\"user\" childKey=\"id\" var=\"userId\" />\n		<var name=\"设置修改时间\" var=\"upTime\" type=\"date\"/>\n		<code name=\"md5\" hasBody=\"true\">\n          def exe(a,cmdHandle){\n          		def pwd=cmdHandle.getVars().get(\"vo\").pwd\n          		def pwd_md5=kkd.common.util.Md5Util.encode(pwd)\n          		cmdHandle.getVars().put(\"pwd\",pwd_md5)\n          }\n      </code>\n		<exeSql name=\"更新数据\" var=\"cnt\" cols=\"pwd,upTime,userId\"\n			sql=\"update user_info set pwd=?,upTime=? where id=?\"\n		>\n			\n		</exeSql>\n        <go name=\"结束1\" to=\"结束\"/>\n	</seg>\n   \n    <seg name=\"结束\">\n        <resp name=\"插入成功\" code=\"200\" msg=\"修改成功\" data=\"\"/>\n        <go  name=\"跳转到结束\" to=\"end\"/>\n        <resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n        <empty name=\"end\"/>\n    </seg>\n	\n</rule>', NULL, NULL, '2021-1-18 14:20:22', '2021-1-18 14:39:11');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (159, 52, 'plugin', 'debug.java', '调试命令', NULL, 'package com.exe.web.debug;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.CmdHandle;\nimport com.exe.base.vo.CmdVo;\nimport com.sun.jdi.*;\nimport com.sun.jdi.connect.AttachingConnector;\nimport com.sun.jdi.connect.Connector;\nimport com.sun.jdi.event.*;\nimport com.sun.jdi.request.BreakpointRequest;\nimport com.sun.jdi.request.EventRequest;\nimport com.sun.jdi.request.EventRequestManager;\nimport com.sun.tools.jdi.LocationImpl;\nimport com.sun.tools.jdi.SocketAttachingConnector;\nimport kkd.common.util.StringUtil;\nimport sun.java2d.loops.GraphicsPrimitive;\n\nimport java.util.HashMap;\nimport java.util.List;\nimport java.util.Map;\n\npublic class DebuggerCmd extends Cmd implements  Runnable{\n\n    public static final String HOST = \"hostname\";\n\n    public static final String PORT = \"port\";\n\n    private VirtualMachine vm;\n\n    private EventQueue eventQueue;\n\n    private EventRequestManager eventRequestManager;\n\n    private Process process;\n\n    private EventSet eventSet;\n\n    private Event event;\n\n    private boolean vmExit = false;\n    private boolean cancelData = false;\n\n    /**\n     * 连接调试服务\n     * @param ip\n     * @param port\n     */\n    public void init(String ip,int port){\n        // 一、取得连接器\n        VirtualMachineManager vmm = Bootstrap.virtualMachineManager();\n        List<AttachingConnector> connectors = vmm.attachingConnectors();\n        SocketAttachingConnector sac = null;\n        for (AttachingConnector ac : connectors) {\n            if (ac instanceof SocketAttachingConnector) {\n                sac = (SocketAttachingConnector) ac;\n                break;\n            }\n        }\n        if (sac == null) {\n            System.out.println(\"JDI error\");\n            return;\n        }\n\n        // 二、连接到远程虚拟器\n        Map<String, Connector.Argument> arguments = sac.defaultArguments();\n        Connector.Argument hostArg = (Connector.Argument) arguments.get(HOST);\n        Connector.Argument portArg = (Connector.Argument) arguments.get(PORT);\n\n        hostArg.setValue(ip);\n        portArg.setValue(String.valueOf(port));\n        try {\n            vm = sac.attach(arguments);\n            process = vm.process();\n            eventRequestManager = vm.eventRequestManager();\n        }catch (Exception e){\n            e.printStackTrace();\n        }\n\n    }\n    /**\n     * 取消断点\n     */\n    public void cancelBreakPoint(String key,int lineNum){\n        // 三、取得要关注的类和方法\n        Class<?> cls=CmdHandle.getCmd(key);\n        List<ReferenceType> classesByName = vm.classesByName(cls.getName());\n        if (classesByName == null || classesByName.size() == 0) {\n            System.out.println(\"No class found\");\n            return;\n        }\n        // 四、注册监听\n        vm.setDebugTraceMode(VirtualMachine.TRACE_EVENTS);\n        vm.resume();\n        try {\n            List<Location> locations = classesByName.get(0).locationsOfLine(lineNum);\n            BreakpointRequest breakpointRequest = eventRequestManager.createBreakpointRequest(locations.get(0));\n            breakpointRequest.disable();\n        }catch (Exception e){\n            e.printStackTrace();\n        }\n    }\n\n    /**\n     * 设置断点\n     */\n    public void setBreakPoint(String key,int lineNum){\n        // 三、取得要关注的类和方法\n        Class<?> cls=CmdHandle.getCmd(key);\n        List<ReferenceType> classesByName = vm.classesByName(cls.getName());\n        if (classesByName == null || classesByName.size() == 0) {\n            System.out.println(\"No class found\");\n            return;\n        }\n        // 四、注册监听\n        vm.setDebugTraceMode(VirtualMachine.TRACE_EVENTS);\n        vm.resume();\n        try {\n            List<Location> locations = classesByName.get(0).locationsOfLine(lineNum);\n            BreakpointRequest breakpointRequest = eventRequestManager.createBreakpointRequest(locations.get(0));\n            breakpointRequest.setSuspendPolicy(EventRequest.SUSPEND_EVENT_THREAD);\n            breakpointRequest.enable();\n        }catch (Exception e){\n            e.printStackTrace();\n        }\n    }\n\n    /**\n     * 取消获取数据\n     */\n    public void cancelData(){\n        cancelData=true;\n    }\n\n\n    /**\n     * 停止\n     */\n    public void stop(){\n        vmExit=true;\n        vm.dispose();\n    }\n\n\n    public static void main(String[] args) throws Exception {\n        DebuggerCmd debugger=new DebuggerCmd();\n        debugger.init(\"127.0.0.1\",8800);\n\n        debugger.setBreakPoint(\"com.exe.bo.debug.Test\",34);\n\n        Value s=debugger.getData(\"com.exe.bo.debug.Test\",34,\"i\");\n        debugger.cancelData();\n        System.out.println(\"sssssssss:\"+s);\n        debugger.cancelBreakPoint(\"com.exe.bo.debug.Test\",34);\n        debugger.stop();\n\n    }\n\n    private  void test() throws Exception {\n        // 三、取得要关注的类和方法\n        List<ReferenceType> classesByName = vm.classesByName(\"com.exe.bo.debug.Test\");\n        if (classesByName == null || classesByName.size() == 0) {\n            System.out.println(\"No class found\");\n            return;\n        }\n        ReferenceType rt = classesByName.get(0);\n        List<Method> methodsByName = rt.methodsByName(\"printHello\");\n        if (methodsByName == null || methodsByName.size() == 0) {\n            System.out.println(\"No method found\");\n            return;\n        }\n        Method method = methodsByName.get(0);\n\n        // 四、注册监听\n        vm.setDebugTraceMode(VirtualMachine.TRACE_EVENTS);\n        vm.resume();\n        List<Location> locations = classesByName.get(0).locationsOfLine(34);\n        BreakpointRequest breakpointRequest = eventRequestManager.createBreakpointRequest(locations.get(0));\n        breakpointRequest.enable();\n\n//        MethodEntryRequest methodEntryRequest = eventRequestManager.createMethodEntryRequest();\n//        methodEntryRequest.addClassFilter(rt);\n//        methodEntryRequest.setSuspendPolicy(EventRequest.SUSPEND_NONE);\n//        // methodEntryRequest.setSuspendPolicy(EventRequest.SUSPEND_ALL);\n//\n//        methodEntryRequest.enable();\n//\n//        BreakpointRequest breakpointRequest = eventRequestManager.createBreakpointRequest(method.location());\n//        breakpointRequest.setSuspendPolicy(EventRequest.SUSPEND_EVENT_THREAD);\n//        // breakpointRequest.setSuspendPolicy(EventRequest.SUSPEND_ALL);\n//        breakpointRequest.enable();\n\n        // ClassPrepareRequest classPrepareRequest = eventRequestManager.createClassPrepareRequest();\n        // classPrepareRequest.addClassFilter(\"test.Test\");\n        // classPrepareRequest.addCountFilter(1);\n        // classPrepareRequest.setSuspendPolicy(EventRequest.SUSPEND_ALL);\n        // classPrepareRequest.enable();\n\n    }\n\n\n\n    public void resume(){\n        eventSet.resume();\n    }\n\n    private  void eventLoop() throws Exception {\n        eventQueue = vm.eventQueue();\n        while (true) {\n            if (vmExit == true) {\n                break;\n            }\n            eventSet = eventQueue.remove();\n            EventIterator eventIterator = eventSet.eventIterator();\n            while (eventIterator.hasNext()) {\n                Event event = (Event) eventIterator.next();\n                execute(event);\n            }\n        }\n    }\n\n    public Value getData(String className,int lineNum,String var){\n        try{\n            cancelData=false;\n            eventQueue = vm.eventQueue();\n            while (true) {\n                if (vmExit == true||cancelData) {\n                    break;\n                }\n                System.out.println(\"----------111111\");\n                eventSet = eventQueue.remove();\n                EventIterator eventIterator = eventSet.eventIterator();\n                while (eventIterator.hasNext()) {\n                    System.out.println(\"----------2222222222\");\n                    Event event = (Event) eventIterator.next();\n                    if(event instanceof BreakpointEvent){\n                        LocationImpl loc= (LocationImpl) ((BreakpointEvent) event).location();\n                        int lineNum1=loc.lineNumber();\n                        String className1=loc.declaringType().name();\n                        System.out.println(\"----------3333333\"+className1+\":\"+lineNum1);\n//                        if(className.equalsIgnoreCase(className1)  && lineNum==lineNum1){\n                            System.out.println(\"----------44444444444444\");\n                            BreakpointEvent breakpointEvent = (BreakpointEvent) event;\n                            System.out.println(\"----------\"+breakpointEvent);\n                            ThreadReference threadReference = breakpointEvent.thread();\n                            StackFrame stackFrame = threadReference.frame(0);\n                            stackFrame.visibleVariables();\n                            LocalVariable localVariable = stackFrame.visibleVariableByName(var);\n                            Value value = stackFrame.getValue(localVariable);\n//                        String date = ((StringReference) value).value();\n                            eventSet.resume();\n                            return value;\n//                        }\n\n                    }\n                    eventSet.resume();\n                }\n                Thread.sleep(1000);\n            }\n\n\n        }catch (Exception e){\n            e.printStackTrace();\n        }\n        return null;\n    }\n\n    private  void execute(Event event) throws Exception {\n        this.event=event;\n        if (event instanceof VMStartEvent) {\n            System.out.println(\"VM started\");\n            eventSet.resume();\n        } else if (event instanceof BreakpointEvent) {\n            System.out.println(\"Reach Method printHello of test.Test\");\n//\n//            BreakpointEvent breakpointEvent = (BreakpointEvent) event;\n//            ThreadReference threadReference = breakpointEvent.thread();\n//            StackFrame stackFrame = threadReference.frame(0);\n//\n//            stackFrame.visibleVariables();\n//\n//            // 获取date变量\n//            LocalVariable localVariable = stackFrame.visibleVariableByName(\"date\");\n//            Value value = stackFrame.getValue(localVariable);\n//            String date = ((StringReference) value).value();\n//\n//            LocalVariable localVariable1 = stackFrame.visibleVariableByName(\"i\");\n//            Value value1 = stackFrame.getValue(localVariable1);\n//            int i = ((IntegerValue) value1).intValue();\n//\n//            System.out.println(\"Debugger print[\" + date + \" : \" + i + \"]\");\n//            eventSet.resume();\n        } else if (event instanceof MethodEntryEvent) {\n            MethodEntryEvent mee = (MethodEntryEvent) event;\n            Method method = mee.method();\n            System.out.println(method.name() + \" was Entered!\");\n            eventSet.resume();\n        } else if (event instanceof VMDisconnectEvent) {\n            vmExit = true;\n        } else if (event instanceof ClassPrepareEvent) {\n            ClassPrepareEvent classPrepareEvent = (ClassPrepareEvent) event;\n            String mainClassName = classPrepareEvent.referenceType().name();\n            if (mainClassName.equals(\"test.Test\")) {\n                System.out.println(\"Class \" + mainClassName + \" is already prepared\");\n            }\n            if (true) {\n                // Get location\n                ReferenceType referenceType = classPrepareEvent.referenceType();\n                List locations = referenceType.locationsOfLine(34);\n                Location location = (Location) locations.get(0);\n\n                // Create BreakpointEvent\n                BreakpointRequest breakpointRequest = eventRequestManager.createBreakpointRequest(location);\n                breakpointRequest.setSuspendPolicy(EventRequest.SUSPEND_ALL);\n                breakpointRequest.enable();\n            }\n            eventSet.resume();\n        } else {\n            eventSet.resume();\n        }\n    }\n\n    private  static Map<String,DebuggerCmd> map=new HashMap<>();\n\n    @Override\n    public void exe(CmdVo cmd, Map<String, String> param) {\n        String op=param.get(\"op\");\n        String var=param.get(\"var\");\n        String debuggerName=param.get(\"debugger\");\n        if(StringUtil.isEmpty(debuggerName)){\n            debuggerName=\"default\";\n        }\n        DebuggerCmd debugger=map.get(debuggerName);\n        if(\"init\".equalsIgnoreCase(op)){\n            String ip=param.get(\"ip\");\n            String port=param.get(\"port\");\n            if(debugger!=null){\n                debugger.stop();\n            }\n            debugger=new DebuggerCmd();\n            debugger.init(ip,Integer.parseInt(port));\n            Thread t=new Thread(debugger);\n            t.start();\n            map.put(debuggerName,debugger);\n            cmdHandle.getVars().put(var,\"success\");\n        }else if(\"setBreakPoint\".equalsIgnoreCase(op)){\n            String className=param.get(\"className\");\n            String lineNum=param.get(\"lineNum\");\n            debugger.setBreakPoint(className,Integer.parseInt(lineNum));\n            cmdHandle.getVars().put(var,\"success\");\n        }else if(\"cancelBreakPoint\".equalsIgnoreCase(op)){\n            String className=param.get(\"className\");\n            String lineNum=param.get(\"lineNum\");\n            debugger.cancelBreakPoint(className,Integer.parseInt(lineNum));\n            cmdHandle.getVars().put(var,\"success\");\n        }else if(\"getData\".equalsIgnoreCase(op)){\n            String className=param.get(\"className\");\n            String lineNum=param.get(\"lineNum\");\n            String key=param.get(\"key\");\n            Value v=debugger.getData(className,Integer.parseInt(lineNum),key);\n            cmdHandle.getVars().put(var,v.toString());\n        }else if(\"cancelData\".equalsIgnoreCase(op)){\n            debugger.cancelData();\n            cmdHandle.getVars().put(var,\"success\");\n        }else if(\"stop\".equalsIgnoreCase(op)){\n            debugger.stop();\n            cmdHandle.getVars().put(var,\"success\");\n        }\n\n    }\n\n    @Override\n    public String op() {\n        return null;\n    }\n\n    @Override\n    public void run() {\n        while (!vmExit){\n            try {\n                Thread.sleep(100);\n            } catch (InterruptedException e) {\n                e.printStackTrace();\n            }\n        }\n    }\n}\n\n', NULL, NULL, '2021-2-2 13:22:21', '2021-2-5 22:00:28');
