INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (162, 52, 'api', '/debug/setBreakPoint', '设置断点', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"设置断点调试\" corn=\"\">\n	<seg name=\"设置断点调试\">\n		<debug name=\"设置断点调试\" op=\"setBreakPoint\" className=\"test\" lineNum=\"24\" var=\"data\"></debug>\n        <resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"data\"/>\n	</seg>\n	\n</rule>', NULL, NULL, '2021-2-2 13:26:27', '2021-2-5 21:03:10');
