INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (164, 52, 'api', '/debug/getData', '获取数据', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"获取数据调试\" corn=\"\">\n	<seg name=\"获取数据调试\">\n		<debug name=\"获取数据调试\" op=\"getData\" className=\"test\" lineNum=\"24\" key=\"date\" var=\"data\"></debug>\n        <resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"data\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-2-2 13:27:15', '2021-2-5 21:56:19');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (165, 52, 'api', '/debug/cancelData', '取消获取数据', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"取消获取数据调试\" corn=\"\">\n	<seg name=\"取消获取数据调试\">\n		<debug name=\"取消获取数据调试\" op=\"cancelData\" var=\"data\"></debug>\n        <resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"data\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-2-2 13:27:39', '2021-2-2 13:39:33');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (166, 52, 'plugin', 'test.java', '测试', NULL, 'package com.exe.web.debug;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\n\nimport java.text.SimpleDateFormat;\nimport java.util.Date;\nimport java.util.Map;\nimport java.util.Random;\n\npublic class TestCmd extends Cmd {\n\n    public static void main(String[] args) {\n\n    }\n\n    protected static void printHello() {\n        Random r = new Random();\n        int i = r.nextInt();\n\n        SimpleDateFormat sdf = new SimpleDateFormat(\"yyyy-MM-dd HH:mm:ss\");\n        String date = sdf.format(new Date());\n\n        System.out.println(date + \" : \" + i);\n    }\n\n    @Override\n    public void exe(CmdVo cmd, Map<String, String> param) {\n        new Thread() {\n\n            @Override\n            public void run() {\n                TestCmd test = new TestCmd();\n                while (true) {\n                    try {\n                        sleep(5000);\n                    } catch (InterruptedException e) {\n                        e.printStackTrace();\n                    }\n                    printHello();\n                }\n            }\n        }.start();\n    }\n\n    @Override\n    public String op() {\n        return null;\n    }\n}			', NULL, NULL, '2021-2-2 13:42:57', '2021-2-2 13:45:57');
