INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (192, 53, 'api', '/cms/cmment/page', 'cms文章评论分页', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"cms文章评论查询\" corn=\"\">\n	<seg name=\"cms文章评论查询\">\n		<param name=\"当前页码\" var=\"page\" key=\"page\" mock=\"@integer(1, 1)\" />\n		<param name=\"每页条数\" var=\"pageSize\" key=\"limit\" mock=\"@integer(10, 10)\" />\n		<param name=\"cms评论标题\" var=\"title\" key=\"title\" />\n		\n		<var name=\"sql\" var=\"sql\" init=\"select * from cms_comment where 1=1 \"/>\n		<append name=\"添加cms评论标题\" key=\"sql\" when=\"title\" value=\" and title like concat(\'%\',?,\'%\') \"/>\n		<page name=\"查询数据\" var=\"list\" cols=\"id, category_id, content_id, title, content, name, ip, create_date, audit_user_id, audit_date, del_flag\" :page=\"#{page}\" :pageSize=\"#{pageSize}\" \n			:sql=\"#{sql}\" param=\"name\"\n		>\n			\n		</page>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>			', NULL, NULL, '2021-4-29 16:07:33', '2021-4-29 16:40:49');
INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (193, 53, 'api', '/cms/cmment/get', 'cms文章评论详情', NULL, '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\n<rule name=\"查询\" corn=\"\">\n	<seg name=\"查询\">\n		<param name=\"资源ID\" var=\"id\" key=\"id\" />\n		<sqlFind name=\"查询数据\" var=\"list\" cols=\"id, category_id, content_id, title, content, name, ip, create_date, audit_user_id, audit_date, del_flag\" param=\"id\"\n		sql=\"select * from cms_comment 1=1 and id=?\" \n		>\n		</sqlFind>\n		\n		<resp name=\"查询成功\" code=\"200\" :msg=\"查询成功\" key=\"list\"/>\n		<go  name=\"跳转到结束\" to=\"end\"/>\n		<resp name=\"失败\" code=\"100\" msg=\"#{errMsg}\" data=\"\"/>\n		<empty name=\"end\"/>\n	</seg>\n	\n</rule>						', NULL, NULL, '2021-4-29 16:08:41', '2021-4-29 16:34:10');
