INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (196, 4, 'plugin', 'upload.java', '上传', NULL, 'package com.exe.base.plugins;\n\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport java.io.*;\nimport java.util.*;\nimport javax.servlet.*;\nimport javax.servlet.http.*;\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\nimport com.exe.web.util.*;\n\npublic class UploadCmd extends Cmd{\n    private final static Logger logger = LoggerFactory.getLogger(UploadCmd.class);\n	@Override\n	public String op() {\n		return \"upload\";\n	}\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n        HttpServletRequest request= (HttpServletRequest) cmdHandle.getSys().get(\"req\");\n        //定义上载文件的最大字节\n        int MAX_SIZE = 102400 * 102400;\n        // 创建根路径的保存变量\n        String rootPath;\n        //声明文件读入类\n        DataInputStream in = null;\n        FileOutputStream fileOut = null;\n        //取得客户端的网络地址\n        String remoteAddr = request.getRemoteAddr();\n\n        //获得服务器的名字\n        String serverName = request.getServerName();\n        logger.info(\"----serverName:$serverName\");\n\n        //取得互联网程序的绝对地址\n        //String realPath = request.getRealPath(serverName);\n\n        //realPath = realPath.substring(0,realPath.lastIndexOf(\"\\\\\"));\n\n        //创建文件的保存目录\n        rootPath = MyConfUtil.getStaticPath() + \"\\\\upload\\\\\";\n\n        //取得客户端上传的数据类型\n        String contentType = request.getContentType();\n        try{\n        if(contentType.indexOf(\"multipart/form-data\") >= 0){\n        //读入上传的数据\n        in = new DataInputStream(request.getInputStream());\n        int formDataLength = request.getContentLength();\n        if(formDataLength > MAX_SIZE){\n        out.println(\"<P>上传的文件字节数不可以超过\" + MAX_SIZE + \"</p>\");\n        return;\n        }\n   