INSERT INTO `exe_resource` (`id`, `appId`, `type`, `path`, `ruleName`, `corn`, `caiConfig`, `caiRule`, `loginRule`, `createTime`, `upTime`) VALUES (198, 4, 'plugin', 'Base64Convert.java', 'base64和文件互相转换', NULL, 'package com.exe.base.plugins;\n\nimport java.util.Map;\n\nimport com.exe.base.Cmd;\nimport com.exe.base.vo.CmdVo;\nimport java.io.*;\nimport java.util.*;\nimport javax.servlet.*;\nimport javax.servlet.http.*;\nimport org.slf4j.Logger;\nimport org.slf4j.LoggerFactory;\nimport com.exe.web.util.*;\nimport kkd.common.util.FileUtil;\nimport sun.misc.BASE64Decoder;\nimport sun.misc.BASE64Encoder;\n\nimport java.io.*;\n\npublic class Base64ConvertCmd extends Cmd{\n    private final static Logger logger = LoggerFactory.getLogger(Base64ConvertCmd.class);\n	@Override\n	public String op() {\n		return \"Base64ConvertCmd\";\n	}\n\n	@Override\n	public void exe(CmdVo caiAction, Map<String, String> param) {\n		Map<String, Object> sys=cmdHandle.getSys();\n        HttpServletRequest request= (HttpServletRequest) cmdHandle.getSys().get(\"req\");\n        StringBuilder sb=new StringBuilder();\n        String staticPath=MyConfUtil.getStaticPath();\n        FileUtil.readFileByLines(staticPath+\"\\\\logs\\\\test.txt\",new FileUtil.FileUtilReader(){\n            @Override\n            public void read(Object o) {\n                    sb.append(o);\n            }\n        });\n        String newBinFile=staticPath+\"\\\\logs\\\\nginx-1.18.0.tar.gz\"; //生成的新文件\n        b.base64ToIo(newBinFile,sb.toString());\n	}\n    \n    \n    \n    public String ioToBase64(String fileName) throws IOException {\n        //=\n        String strBase64 = null;\n        try {\n            InputStream in = new FileInputStream(fileName);\n            // in.available()返回文件的字节长度\n            byte[] bytes = new byte[in.available()];\n            // 将文件中的内容读入到数组中\n            in.read(bytes);\n            strBase64 = new BASE64Encoder().encode(bytes);      //将字节流数组转换为字符串\n            in.close();\n        } catch (FileNotFoundException fe) {\n            fe.printStackTrace();\n        } catch (IOException ioe) {\n            ioe.printStackTrace();\n        }\n        return strBase64;\n    }\n\n    public void base64ToIo(String binFile,String strBase64) throws IOException {\n        String string = strBase64;\n        String fileName = binFile;\n        try {\n            // 解码，然后将字节转换为文件\n            byte[] bytes = new BASE64Decoder().decodeBuffer(string);   //将字符串转换为byte数组\n            ByteArrayInputStream in = new ByteArrayInputStream(bytes);\n            byte[] buffer = new byte[1024];\n            FileOutputStream out = new FileOutputStream(fileName);\n            int bytesum = 0;\n            int byteread = 0;\n            while ((byteread = in.read(buffer)) != -1) {\n                bytesum += byteread;\n                out.write(buffer, 0, byteread); //文件写操作\n            }\n        } catch (IOException ioe) {\n            ioe.printStackTrace();\n        }\n    }\n	\n	\n}\n', NULL, NULL, '2021-5-22 21:54:28', '2021-5-22 22:04:11');
